import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { billingService } from "./billing";
import { requireAuth } from "./auth-traditional";
import { 
  insertStudentSchema, 
  insertInvoiceSchema, 
  insertPaymentSchema,
  insertAttendanceSchema,
  insertAssessmentSchema,
  insertGradeSchema,
  insertAnnouncementSchema,
  insertClassScheduleSchema,
  insertScheduleChangeSchema,
  students,
  subjects,
  classes,
  enrollments,
  invoices,
  payments,
  paymentAllocations,
  invoiceAdjustments,
  attendance,
  users,
  expenses,
  assessments,
  grades,
  cashDrawRequests,
  announcements,
  announcementRecipients,
  addOns,
  invoiceItems,
  classSchedules,
  scheduleChanges,
  studentNotifications
} from "@shared/schema";
import { db } from "./db";
import { and, desc, eq, gte, lte, sql } from 'drizzle-orm';
import { ObjectStorageService } from "./objectStorage";

export async function registerRoutes(app: Express): Promise<Server> {
  // Enhanced demo authentication with role-based access control
  app.use((req: any, res, next) => {
    // Parse role from URL query parameter for demo purposes
    const urlRole = req.query.role || 'finance';

    // Demo user configurations for different role types
    const demoUsers = {
      // Regular teacher - only sees teacher dashboard
      'teacher': {
        id: 'demo-teacher-1',
        role: 'teacher',
        isSuperAdmin: false,
        isTeacher: true,
        teacherSubjects: ['99c0039b-c3cb-4182-b311-5d69a755d548', '747f8a50-8cb0-4090-aaf0-1a6dda90103b'],
        firstName: 'Ahmed',
        lastName: 'Khan',
        email: 'teacher1@primax.edu'
      },
      // Super admin who is also a teacher - sees everything
      'super-admin-teacher': {
        id: 'demo-super-admin-teacher',
        role: 'teacher', // Base role is teacher
        isSuperAdmin: true,
        isTeacher: true,
        teacherSubjects: ['99c0039b-c3cb-4182-b311-5d69a755d548', '747f8a50-8cb0-4090-aaf0-1a6dda90103b'],
        firstName: 'Sarah',
        lastName: 'Ahmed',
        email: 'sarah.admin@primax.edu'
      },
      // Super admin management (not teacher) - sees finance, management, parent
      'super-admin-management': {
        id: 'demo-super-admin-mgmt',
        role: 'management',
        isSuperAdmin: true,
        isTeacher: false,
        teacherSubjects: [],
        firstName: 'Hassan',
        lastName: 'Ali',
        email: 'hassan.admin@primax.edu'
      },
      // Regular finance user
      'finance': {
        id: 'demo-finance-user',
        role: 'finance',
        isSuperAdmin: false,
        isTeacher: false,
        teacherSubjects: [],
        firstName: 'Demo',
        lastName: 'User',
        email: 'demo@primax.school'
      }
    };

    // Select user based on role parameter
    const user = demoUsers[urlRole as keyof typeof demoUsers] || demoUsers['finance'];

    req.user = {
      ...user,
      expires_at: Math.floor(Date.now() / 1000) + 3600 // 1 hour from now
    };
    req.isAuthenticated = () => true;
    next();
  });

  // Enhanced auth route with role-based access info
  app.get('/api/auth/user', async (req: any, res) => {
    const user = req.user;

    // Determine accessible dashboards based on role and permissions
    const accessibleDashboards = getUserAccessibleDashboards(user);

    res.json({
      ...user,
      accessibleDashboards
    });
  });

  // Helper function to determine accessible dashboards
  function getUserAccessibleDashboards(user: any): string[] {
    const dashboards = [];

    if (user.isSuperAdmin) {
      // Super admins get access to finance, management, and parent dashboards
      dashboards.push('finance', 'management', 'parent');

      // Super admin teachers also get teacher dashboard
      if (user.isTeacher) {
        dashboards.push('teacher');
      }
    } else {
      // Regular users only get their primary role dashboard
      dashboards.push(user.role);
    }

    return dashboards;
  }

  // Dashboard stats
  app.get("/api/dashboard/stats", async (req, res) => {
    try {
      const stats = await storage.getDashboardStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // Students routes
  app.get("/api/students", async (req, res) => {
    try {
      const students = await storage.getStudents();
      res.json(students);
    } catch (error) {
      console.error("Error fetching students:", error);
      res.status(500).json({ message: "Failed to fetch students" });
    }
  });

  // Toggle student active status
  app.patch("/api/students/:id/toggle-active", async (req, res) => {
    try {
      const { id } = req.params;
      const { isActive } = req.body;

      if (typeof isActive !== 'boolean') {
        return res.status(400).json({ message: "isActive must be a boolean" });
      }

      const updatedStudent = await storage.toggleStudentActiveStatus(id, isActive);
      res.json(updatedStudent);
    } catch (error) {
      console.error("Error toggling student active status:", error);
      res.status(500).json({ message: "Failed to update student status" });
    }
  });

  // Delete student permanently
  app.delete("/api/students/:id", async (req, res) => {
    try {
      const { id } = req.params;

      // Check if student exists first
      const student = await storage.getStudent(id);
      if (!student) {
        return res.status(404).json({ message: "Student not found" });
      }

      await storage.deleteStudent(id);
      res.json({ message: "Student deleted successfully" });
    } catch (error) {
      console.error("Error deleting student:", error);
      res.status(500).json({ message: "Failed to delete student" });
    }
  });

  // Roll number management endpoints
  app.get("/api/roll-numbers/next", async (req, res) => {
    try {
      const nextRollNumber = await storage.getNextRollNumber();
      res.json({ 
        nextRollNumber,
        format: "PMXyy-#### (PMX + year + random 4-digit number)",
        example: "PMX25-4782"
      });
    } catch (error) {
      console.error("Error generating next roll number:", error);
      res.status(500).json({ message: "Failed to generate roll number" });
    }
  });

  app.post("/api/roll-numbers/reserve", async (req, res) => {
    try {
      const reservation = await storage.reserveRollNumber();
      res.json({
        rollNumber: reservation.rollNumber,
        expiresAt: reservation.expiresAt,
        format: "PMXyy-#### (PMX + year + random 4-digit number)"
      });
    } catch (error) {
      console.error("Error reserving roll number:", error);
      res.status(500).json({ message: "Failed to reserve roll number" });
    }
  });

