client/src/pages/finance/AttendanceManag.content:
import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Check, X, Clock, QrCode, Users, Calendar, School, ChevronRight } from "lucide-react";
import QrScanner from "qr-scanner";

interface Student {
  id: string;
  firstName: string;
  lastName: string;
  rollNumber: string;
  profileImageUrl?: string;
}

interface Class {
  id: string;
  name: string;
  subject: string;
  startTime: string;
  endTime: string;
  dayOfWeek: number;
  teacherName: string;
  teacherId: string;
}

interface AttendanceRecord {
  studentId: string;
  status: 'present' | 'absent' | 'late';
}

export default function AttendanceManagement() {
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [selectedClass, setSelectedClass] = useState("");
  const [attendanceRecords, setAttendanceRecords] = useState<AttendanceRecord[]>([]);
  const [attendanceMethod, setAttendanceMethod] = useState<'tap' | 'qr'>('tap');
  const [isScanning, setIsScanning] = useState(false);
  const [qrScanner, setQrScanner] = useState<QrScanner | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const videoRef = useRef<HTMLVideoElement>(null);

  // Fetch all classes for the selected date
  const { data: allClasses, isLoading: classesLoading } = useQuery<Class[]>({
    queryKey: ['/api/classes/all', selectedDate],
    queryFn: async () => {
      // This would need to be implemented on the backend
      const response = await fetch(`/api/classes/all?date=${selectedDate}`);
      return response.ok ? response.json() : [];
    }
  });

  const { data: students, isLoading: studentsLoading } = useQuery<Student[]>({
    queryKey: ['/api/classes', selectedClass, 'students'],
    enabled: !!selectedClass,
  });

  // Get existing attendance for the selected class and date
  const { data: existingAttendance } = useQuery({
    queryKey: ['/api/attendance', selectedClass, selectedDate],
    enabled: !!selectedClass,
    queryFn: async () => {
      const response = await fetch(`/api/attendance?classId=${selectedClass}&date=${selectedDate}`);
      return response.ok ? response.json() : [];
    }
  });

  // Update attendance records when existing attendance is loaded
  useEffect(() => {
    if (existingAttendance) {
      const records = existingAttendance.map((att: any) => ({
        studentId: att.studentId,
        status: att.status
      }));
      setAttendanceRecords(records);
    }
  }, [existingAttendance]);

  // Check if attendance has been taken for a class
  const getClassAttendanceStatus = (classId: string) => {
    if (classId === selectedClass && existingAttendance && existingAttendance.length > 0) {
      return 'taken';
    }
    return 'not-taken';
  };

  const submitAttendanceMutation = useMutation({
    mutationFn: async () => {
      const promises = attendanceRecords.map(record => 
        apiRequest('POST', '/api/attendance', {
          classId: selectedClass,
          studentId: record.studentId,
          attendanceDate: selectedDate,
          status: record.status,
        })
      );
      return Promise.all(promises);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Attendance updated successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update attendance. Please try again.",
        variant: "destructive",
      });
    },
  });

  // QR Scanner setup
  useEffect(() => {
    if (attendanceMethod === 'qr' && videoRef.current && !qrScanner && isScanning) {
      const scanner = new QrScanner(
        videoRef.current,
        (result: QrScanner.ScanResult) => handleQrScan(result.data),
        {
          preferredCamera: 'environment',
          highlightScanRegion: true,
          highlightCodeOutline: true,
        }
      );
      setQrScanner(scanner);
      scanner.start();
    }

    return () => {
      if (qrScanner) {
        qrScanner.stop();
        qrScanner.destroy();
        setQrScanner(null);
      }
    };
  }, [attendanceMethod, isScanning]);

  const handleQrScan = (qrData: string) => {
    try {
      const studentId = qrData.includes(':') ? qrData.split(':')[0] : qrData;
      const student = students?.find(s => s.id === studentId || s.rollNumber === qrData);

      if (student) {
        markAttendance(student.id, 'present');
        toast({
          title: "Student Scanned",
          description: `${student.firstName} ${student.lastName} marked as present`,
        });
      } else {
        toast({
          title: "Student Not Found",
          description: "This QR code doesn't match any student in the current class",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Invalid QR Code",
        description: "Unable to read student information from this QR code",
        variant: "destructive",
      });
    }
  };

  const toggleScanning = () => {
    if (isScanning && qrScanner) {
      qrScanner.stop();
      setIsScanning(false);
    } else {
      setIsScanning(true);
    }
  };

  const markAttendance = (studentId: string, status: 'present' | 'absent' | 'late') => {
    setAttendanceRecords(prev => {
      const existing = prev.find(r => r.studentId === studentId);
      if (existing) {
        return prev.map(r => r.studentId === studentId ? { ...r, status } : r);
      }
      return [...prev, { studentId, status }];
    });
  };

  const undoMark = (studentId: string) => {
    setAttendanceRecords(prev => prev.filter(r => r.studentId !== studentId));
  };

  const getStudentStatus = (studentId: string): 'present' | 'absent' | 'late' | undefined => {
    return attendanceRecords.find(r => r.studentId === studentId)?.status;
  };

  const handleSubmit = () => {
    if (!selectedClass) {
      toast({
        title: "Error",
        description: "Please select a class first.",
        variant: "destructive",
      });
      return;
    }
    submitAttendanceMutation.mutate();
  };

  const presentCount = attendanceRecords.filter(r => r.status === 'present').length;
  const absentCount = attendanceRecords.filter(r => r.status === 'absent').length;
  const lateCount = attendanceRecords.filter(r => r.status === 'late').length;
  const totalStudents = students?.length || 0;
  const unmarkedCount = totalStudents - attendanceRecords.length;

  // Group classes by teacher
  const classesByTeacher = allClasses?.reduce((acc, cls) => {
    if (!acc[cls.teacherId]) {
      acc[cls.teacherId] = {
        teacherName: cls.teacherName,
        classes: []
      };
    }
    acc[cls.teacherId].classes.push(cls);
    return acc;
  }, {} as Record<string, { teacherName: string; classes: Class[] }>) || {};

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center gap-2">
                <School className="h-5 w-5" />
                Attendance
                <span className="text-sm font-normal text-gray-500">(Front Desk)</span>
              </CardTitle>
              <p className="text-gray-600 mt-1 flex items-center gap-2">
                <Calendar className="h-4 w-4" />
                Manage attendance for all classes and teachers
              </p>
            </div>
            {selectedClass && (
              <div className="grid grid-cols-4 gap-4 text-center">
                <div>
                  <div className="flex items-center justify-center gap-1">
                    <Check className="h-4 w-4 text-green-600" />
                    <span className="text-sm text-gray-600">Present</span>
                  </div>
                  <p className="text-2xl font-bold text-green-600">
                    {presentCount}
                  </p>
                </div>
                <div>
                  <div className="flex items-center justify-center gap-1">
                    <X className="h-4 w-4 text-red-600" />
                    <span className="text-sm text-gray-600">Absent</span>
                  </div>
                  <p className="text-2xl font-bold text-red-600">
                    {absentCount}
                  </p>
                </div>
                <div>
                  <div className="flex items-center justify-center gap-1">
                    <Clock className="h-4 w-4 text-yellow-600" />
                    <span className="text-sm text-gray-600">Late</span>
                  </div>
                  <p className="text-2xl font-bold text-yellow-600">
                    {lateCount}
                  </p>
                </div>
                <div>
                  <div className="flex items-center justify-center gap-1">
                    <Users className="h-4 w-4 text-gray-600" />
                    <span className="text-sm text-gray-600">Unmarked</span>
                  </div>
                  <p className="text-2xl font-bold text-gray-600">
                    {unmarkedCount}
                  </p>
                </div>
              </div>
            )}
          </div>
        </CardHeader>

        <CardContent className="space-y-4">
          {/* Date Selection */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Select Date</label>
              <input
                type="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                data-testid="input-date"
              />
            </div>
          </div>

          {/* Classes by Teacher */}
          {classesLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {[1, 2, 3, 4, 5, 6].map((i) => (
                <div key={i} className="animate-pulse bg-gray-200 h-24 rounded-lg"></div>
              ))}
            </div>
          ) : (
            <div className="space-y-6">
              {Object.entries(classesByTeacher).map(([teacherId, { teacherName, classes }]) => (
                <div key={teacherId}>
                  <h3 className="text-lg font-medium text-gray-900 mb-3 flex items-center gap-2">
                    <Users className="h-5 w-5" />
                    {teacherName}
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {classes.map((classItem) => (
                      <Card 
                        key={classItem.id}
                        className={`cursor-pointer transition-all ${
                          selectedClass === classItem.id 
                            ? 'bg-blue-50 border-blue-500 shadow-md' 
                            : 'hover:bg-gray-50 border-gray-200'
                        }`}
                        onClick={() => {
                          setSelectedClass(classItem.id);
                          setAttendanceRecords([]);
                        }}
                        data-testid={`card-class-${classItem.id}`}
                      >
                        <CardContent className="pt-4">
                          <div className="flex items-center justify-between">
                            <div>
                              <div className="flex items-center gap-2">
                                <h4 className="font-medium text-gray-900">{classItem.subject}</h4>
                                {/* Show attendance status */}
                                {existingAttendance && existingAttendance.length > 0 && selectedClass === classItem.id && (
                                  <Badge variant="secondary" className="bg-green-100 text-green-800">
                                    ‚úì Taken
                                  </Badge>
                                )}
                              </div>
                              <p className="text-sm text-gray-600">
                                {classItem.startTime} - {classItem.endTime}
                              </p>
                              <p className="text-xs text-gray-500">
                                {classItem.name}
                              </p>
                            </div>
                            <ChevronRight 
                              className={`h-5 w-5 ${
                                selectedClass === classItem.id ? 'text-blue-500' : 'text-gray-400'
                              }`} 
                            />
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </div>
              ))}

              {Object.keys(classesByTeacher).length === 0 && (
                <div className="text-center py-12">
                  <Calendar className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No Classes Found</h3>
                  <p className="text-gray-600">
                    No classes are scheduled for {new Date(selectedDate).toLocaleDateString()}
                  </p>
                </div>
              )}
            </div>
          )}

          {/* Attendance Interface */}
          {selectedClass && (
            <Tabs value={attendanceMethod} onValueChange={(value) => setAttendanceMethod(value as 'tap' | 'qr')} className="w-full">
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="tap" className="flex items-center gap-2">
                  <Users className="h-4 w-4" />
                  Tap Interface
                </TabsTrigger>
                <TabsTrigger value="qr" className="flex items-center gap-2">
                  <QrCode className="h-4 w-4" />
                  QR Scanner
                </TabsTrigger>
              </TabsList>

              <TabsContent value="tap" className="mt-6">
                {studentsLoading ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {[1, 2, 3, 4, 5, 6].map((i) => (
                      <div key={i} className="animate-pulse bg-gray-200 h-20 rounded-lg"></div>
                    ))}
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {students?.map((student) => {
                      const status = getStudentStatus(student.id);
                      const isMarked = !!status;

                      return (
                        <Card 
                          key={student.id} 
                          className={`${
                            status === 'present' ? 'bg-green-50 border-green-200' :
                            status === 'absent' ? 'bg-red-50 border-red-200' :
                            status === 'late' ? 'bg-yellow-50 border-yellow-200' :
                            'border-gray-200'
                          }`}
                        >
                          <CardContent className="pt-4">
                            <div className="flex items-center justify-between">
                              <div className="flex items-center">
                                <div className="w-10 h-10 bg-gray-300 rounded-full mr-3 flex items-center justify-center text-xs font-bold text-gray-700">
                                  {student.rollNumber}
                                </div>
                                <div>
                                  <p className="text-sm font-medium text-gray-900">
                                    {student.firstName} {student.lastName}
                                  </p>
                                  <p className="text-xs text-gray-500">
                                    Roll: {student.rollNumber}
                                  </p>
                                </div>
                              </div>

                              {isMarked ? (
                                <div className="flex items-center space-x-1">
                                  <Badge 
                                    className={
                                      status === 'present' ? 'bg-green-100 text-green-800' :
                                      status === 'absent' ? 'bg-red-100 text-red-800' :
                                      'bg-yellow-100 text-yellow-800'
                                    }
                                  >
                                    {status}
                                  </Badge>
                                  <Button
                                    size="sm"
                                    variant="ghost"
                                    onClick={() => undoMark(student.id)}
                                    className="w-6 h-6 p-0"
                                  >
                                    <X className="h-3 w-3" />
                                  </Button>
                                </div>
                              ) : (
                                <div className="flex space-x-1">
                                  <Button
                                    size="sm"
                                    className="w-8 h-8 p-0 bg-green-600 hover:bg-green-700"
                                    onClick={() => markAttendance(student.id, 'present')}
                                    title="Mark as Present"
                                  >
                                    <Check className="h-3 w-3" />
                                  </Button>
                                  <Button
                                    size="sm"
                                    className="w-8 h-8 p-0 bg-red-600 hover:bg-red-700"
                                    onClick={() => markAttendance(student.id, 'absent')}
                                    title="Mark as Absent"
                                  >
                                    <X className="h-3 w-3" />
                                  </Button>
                                  <Button
                                    size="sm"
                                    className="w-8 h-8 p-0 bg-yellow-600 hover:bg-yellow-700"
                                    onClick={() => markAttendance(student.id, 'late')}
                                    title="Mark as Late"
                                  >
                                    <Clock className="h-3 w-3" />
                                  </Button>
                                </div>
                              )}
                            </div>
                          </CardContent>
                        </Card>
                      );
                    })}
                  </div>
                )}
              </TabsContent>

              <TabsContent value="qr" className="mt-6">
                <Card>
                  <CardContent className="p-6">
                    <div className="text-center mb-4">
                      <QrCode className="h-12 w-12 text-gray-400 mx-auto mb-2" />
                      <h3 className="text-lg font-medium text-gray-900 mb-2">QR Code Scanner</h3>
                      <p className="text-gray-600 mb-4">
                        Scan student QR codes to automatically mark attendance as present
                      </p>
                    </div>

                    <div className="space-y-4">
                      <div className="flex justify-center">
                        <Button 
                          onClick={toggleScanning}
                          className={isScanning ? "bg-red-600 hover:bg-red-700" : "bg-green-600 hover:bg-green-700"}
                        >
                          {isScanning ? (
                            <>
                              <X className="h-4 w-4 mr-2" />
                              Stop Scanner
                            </>
                          ) : (
                            <>
                              <QrCode className="h-4 w-4 mr-2" />
                              Start Scanner
                            </>
                          )}
                        </Button>
                      </div>

                      {isScanning && (
                        <div className="relative">
                          <video 
                            ref={videoRef}
                            className="w-full max-w-md mx-auto rounded-lg border-2 border-dashed border-gray-300"
                            style={{ aspectRatio: '1/1' }}
                          />
                          <div className="text-center mt-2">
                            <p className="text-sm text-gray-600">
                              Hold student QR codes within the camera view
                            </p>
                          </div>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>

              {/* Submit Button */}
              <div className="flex justify-end pt-6">
                <Button
                  onClick={handleSubmit}
                  disabled={submitAttendanceMutation.isPending || attendanceRecords.length === 0}
                  className="min-w-[200px]"
                >
                  {submitAttendanceMutation.isPending ? 'Updating...' : 'Update Attendance'}
                </Button>
              </div>
            </Tabs>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/finance/CashDrawApprova.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { CashDrawRequest } from "@shared/schema";

export default function CashDrawApprovals() {
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [selectedRequest, setSelectedRequest] = useState<CashDrawRequest | null>(null);
  const [reviewNotes, setReviewNotes] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: requests, isLoading } = useQuery<CashDrawRequest[]>({
    queryKey: ['/api/cash-draw-requests'],
  });

  const updateRequestMutation = useMutation({
    mutationFn: async ({ id, status, notes }: { id: string; status: string; notes: string }) => {
      return await apiRequest('PATCH', `/api/cash-draw-requests/${id}`, {
        status,
        notes,
      });
    },
    onSuccess: (_, variables) => {
      toast({
        title: "Success", 
        description: `Request ${variables.status} successfully!`,
        variant: "success" as any,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/cash-draw-requests'] });
      setSelectedRequest(null);
      setReviewNotes("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update request. Please try again.",
        variant: "destructive",
      });
    },
  });

  const filteredRequests = requests?.filter(request => {
    const matchesSearch = searchQuery === "" || 
      request.teacherName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      request.reason.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = statusFilter === "all" || request.status === statusFilter;

    return matchesSearch && matchesStatus;
  }) || [];

  const handleApprove = (request: CashDrawRequest) => {
    updateRequestMutation.mutate({
      id: request.id,
      status: 'approved',
      notes: reviewNotes,
    });
  };

  const handleDeny = (request: CashDrawRequest) => {
    updateRequestMutation.mutate({
      id: request.id,
      status: 'denied',
      notes: reviewNotes,
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'approved':
        return 'bg-green-100 text-green-800';
      case 'denied':
        return 'bg-red-100 text-red-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'approved':
        return 'fas fa-check-circle text-green-600';
      case 'denied':
        return 'fas fa-times-circle text-red-600';
      case 'pending':
        return 'fas fa-clock text-yellow-600';
      default:
        return 'fas fa-question-circle text-gray-600';
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="h-64 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  const pendingCount = requests?.filter(r => r.status === 'pending').length || 0;
  const totalAmount = filteredRequests.reduce((sum, r) => sum + Number(r.amount), 0);

  return (
    <div className="space-y-6">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-yellow-100 rounded-lg">
                <i className="fas fa-clock text-yellow-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending Requests</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-pending-requests">
                  {pendingCount}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <i className="fas fa-money-bill-wave text-blue-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Amount</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-total-amount">
                  Rs. {totalAmount.toLocaleString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <i className="fas fa-list text-green-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Requests</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-total-requests">
                  {requests?.length || 0}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Cash Draw Requests</CardTitle>
            <div className="flex space-x-3">
              <div className="relative">
                <Input
                  placeholder="Search requests..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                  data-testid="input-search-requests"
                />
                <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
              </div>
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                data-testid="select-status-filter"
              >
                <option value="all">All Status</option>
                <option value="pending">Pending</option>
                <option value="approved">Approved</option>
                <option value="denied">Denied</option>
              </select>
            </div>
          </div>
        </CardHeader>

        <CardContent>
          <div className="overflow-x-auto">
            <table className="w-full text-sm">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Teacher</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Amount</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Reason</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Requested</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Status</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Actions</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {filteredRequests.length > 0 ? filteredRequests.map((request) => (
                  <tr key={request.id} className="hover:bg-gray-50" data-testid={`row-request-${request.id}`}>
                    <td className="px-4 py-3">
                      <div className="flex items-center">
                        <i className="fas fa-user-tie text-gray-400 mr-3"></i>
                        <div>
                          <p className="font-medium text-gray-900" data-testid={`text-teacher-${request.id}`}>
                            {request.teacherName || 'Teacher Name'}
                          </p>
                          <p className="text-xs text-gray-500">ID: {request.teacherId}</p>
                        </div>
                      </div>
                    </td>
                    <td className="px-4 py-3">
                      <span className="font-semibold text-gray-900" data-testid={`text-amount-${request.id}`}>
                        Rs. {Number(request.amount).toLocaleString()}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <p className="text-gray-700 max-w-xs truncate" data-testid={`text-reason-${request.id}`}>
                        {request.reason}
                      </p>
                    </td>
                    <td className="px-4 py-3">
                      <div className="text-sm" data-testid={`text-requested-${request.id}`}>
                        <p>{new Date(request.requestedAt).toLocaleDateString()}</p>
                        <p className="text-gray-500">{new Date(request.requestedAt).toLocaleTimeString()}</p>
                      </div>
                    </td>
                    <td className="px-4 py-3">
                      <Badge className={getStatusColor(request.status)} data-testid={`badge-status-${request.id}`}>
                        <i className={`${getStatusIcon(request.status)} mr-1`}></i>
                        {request.status.toUpperCase()}
                      </Badge>
                    </td>
                    <td className="px-4 py-3">
                      {request.status === 'pending' ? (
                        <div className="flex space-x-2">
                          <Dialog>
                            <DialogTrigger asChild>
                              <Button 
                                size="sm" 
                                className="bg-green-600 hover:bg-green-700"
                                onClick={() => setSelectedRequest(request)}
                                data-testid={`button-approve-${request.id}`}
                              >
                                <i className="fas fa-check mr-1"></i>
                                Approve
                              </Button>
                            </DialogTrigger>
                            <DialogContent>
                              <DialogHeader>
                                <DialogTitle>Approve Cash Draw Request</DialogTitle>
                              </DialogHeader>
                              <div className="space-y-4">
                                <div className="bg-gray-50 p-4 rounded-lg">
                                  <p><strong>Teacher:</strong> {request.teacherName}</p>
                                  <p><strong>Amount:</strong> Rs. {Number(request.amount).toLocaleString()}</p>
                                  <p><strong>Reason:</strong> {request.reason}</p>
                                </div>
                                <div>
                                  <Label htmlFor="approveNotes">Approval Notes (Optional)</Label>
                                  <Textarea
                                    id="approveNotes"
                                    placeholder="Add any notes for this approval..."
                                    value={reviewNotes}
                                    onChange={(e) => setReviewNotes(e.target.value)}
                                    data-testid="textarea-approve-notes"
                                  />
                                </div>
                                <div className="flex justify-end space-x-2">
                                  <Button variant="outline" onClick={() => setSelectedRequest(null)}>
                                    Cancel
                                  </Button>
                                  <Button 
                                    onClick={() => handleApprove(request)}
                                    disabled={updateRequestMutation.isPending}
                                    data-testid="button-confirm-approve"
                                  >
                                    {updateRequestMutation.isPending ? 'Processing...' : 'Approve Request'}
                                  </Button>
                                </div>
                              </div>
                            </DialogContent>
                          </Dialog>

                          <Dialog>
                            <DialogTrigger asChild>
                              <Button 
                                size="sm" 
                                variant="destructive"
                                onClick={() => setSelectedRequest(request)}
                                data-testid={`button-deny-${request.id}`}
                              >
                                <i className="fas fa-times mr-1"></i>
                                Deny
                              </Button>
                            </DialogTrigger>
                            <DialogContent>
                              <DialogHeader>
                                <DialogTitle>Deny Cash Draw Request</DialogTitle>
                              </DialogHeader>
                              <div className="space-y-4">
                                <div className="bg-gray-50 p-4 rounded-lg">
                                  <p><strong>Teacher:</strong> {request.teacherName}</p>
                                  <p><strong>Amount:</strong> Rs. {Number(request.amount).toLocaleString()}</p>
                                  <p><strong>Reason:</strong> {request.reason}</p>
                                </div>
                                <div>
                                  <Label htmlFor="denyNotes">Reason for Denial *</Label>
                                  <Textarea
                                    id="denyNotes"
                                    placeholder="Provide a reason for denying this request..."
                                    value={reviewNotes}
                                    onChange={(e) => setReviewNotes(e.target.value)}
                                    required
                                    data-testid="textarea-deny-notes"
                                  />
                                </div>
                                <div className="flex justify-end space-x-2">
                                  <Button variant="outline" onClick={() => setSelectedRequest(null)}>
                                    Cancel
                                  </Button>
                                  <Button 
                                    variant="destructive"
                                    onClick={() => handleDeny(request)}
                                    disabled={!reviewNotes.trim() || updateRequestMutation.isPending}
                                    data-testid="button-confirm-deny"
                                  >
                                    {updateRequestMutation.isPending ? 'Processing...' : 'Deny Request'}
                                  </Button>
                                </div>
                              </div>
                            </DialogContent>
                          </Dialog>
                        </div>
                      ) : (
                        <div className="text-sm text-gray-500">
                          {request.reviewedAt && (
                            <>
                              <p>Reviewed: {new Date(request.reviewedAt).toLocaleDateString()}</p>
                              {request.notes && <p className="text-xs">Notes: {request.notes}</p>}
                            </>
                          )}
                        </div>
                      )}
                    </td>
                  </tr>
                )) : (
                  <tr>
                    <td colSpan={6} className="px-4 py-8 text-center text-gray-500">
                      <i className="fas fa-inbox text-4xl mb-4"></i>
                      <p>No cash draw requests found</p>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/finance/DailyClose.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { CalendarIcon } from "lucide-react";
import { format } from "date-fns";
import { cn } from "@/lib/utils";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { DailyClose } from "@shared/schema";

export default function DailyClose() {
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [calendarDate, setCalendarDate] = useState<Date>(new Date());
  const [totalCash, setTotalCash] = useState("");
  const [totalBank, setTotalBank] = useState("");
  const [notes, setNotes] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Function to handle date selection
  const handleDateSelect = (date: Date | undefined) => {
    if (date) {
      const dateString = date.toISOString().split('T')[0];
      setSelectedDate(dateString);
      setCalendarDate(date);
      // Clear form when changing dates
      setTotalCash("");
      setTotalBank("");
      setNotes("");
    }
  };

  // Navigate to previous day
  const goToPreviousDay = () => {
    const currentDate = new Date(selectedDate);
    currentDate.setDate(currentDate.getDate() - 1);
    const newDateString = currentDate.toISOString().split('T')[0];
    setSelectedDate(newDateString);
    setCalendarDate(currentDate);
    // Clear form when changing dates
    setTotalCash("");
    setTotalBank("");
    setNotes("");
  };

  // Navigate to next day (only if not going into future)
  const goToNextDay = () => {
    const currentDate = new Date(selectedDate);
    currentDate.setDate(currentDate.getDate() + 1);
    const today = new Date().toISOString().split('T')[0];
    const newDateString = currentDate.toISOString().split('T')[0];

    // Only allow navigation if not going into future
    if (newDateString <= today) {
      setSelectedDate(newDateString);
      setCalendarDate(currentDate);
      // Clear form when changing dates
      setTotalCash("");
      setTotalBank("");
      setNotes("");
    }
  };

  const { data: dailyCloseRecord, isLoading } = useQuery<DailyClose | null>({
    queryKey: ['/api/daily-close', selectedDate],
  });

  const { data: payments } = useQuery({
    queryKey: ['/api/payments'],
  });

  const createDailyCloseMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest('POST', '/api/daily-close', data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Daily close completed successfully!",
        variant: "success" as any,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/daily-close'] });
      setTotalCash("");
      setTotalBank("");
      setNotes("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to complete daily close. Please try again.",
        variant: "destructive",
      });
    },
  });

  const lockDayMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest('POST', '/api/daily-close/lock', data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Daily close locked and finalized successfully!",
        variant: "success" as any,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/daily-close'] });
      setTotalCash("");
      setTotalBank("");
      setNotes("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to lock daily close. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = () => {
    const cash = parseFloat(totalCash) || 0;
    const bank = parseFloat(totalBank) || 0;
    const variance = cash + bank - (expectedTotal || 0);

    createDailyCloseMutation.mutate({
      closeDate: selectedDate,
      totalCash: cash,
      totalBank: bank,
      expectedCash,
      expectedBank,
      expectedTotal,
      actualTotal: cash + bank,
      variance,
      notes,
    });
  };

  const handleLockDay = () => {
    const cash = parseFloat(totalCash) || 0;
    const bank = parseFloat(totalBank) || 0;
    const variance = cash + bank - (expectedTotal || 0);

    const dailyCloseData = {
      closeDate: selectedDate,
      totalCash: cash,
      totalBank: bank,
      expectedCash,
      expectedBank,
      expectedTotal,
      actualTotal: cash + bank,
      variance,
      notes,
    };

    lockDayMutation.mutate({
      closeDate: selectedDate,
      dailyCloseData,
    });
  };

  // Calculate expected totals from payments
  const todaysPayments = payments?.filter((payment: any) => 
    new Date(payment.paymentDate).toDateString() === new Date(selectedDate).toDateString()
  ) || [];

  const expectedCash = todaysPayments
    .filter((p: any) => p.paymentMethod === 'cash')
    .reduce((sum: number, p: any) => sum + Number(p.amount), 0);

  const expectedBank = todaysPayments
    .filter((p: any) => ['bank_transfer', 'card'].includes(p.paymentMethod))
    .reduce((sum: number, p: any) => sum + Number(p.amount), 0);

  const expectedTotal = expectedCash + expectedBank;
  const actualTotal = (parseFloat(totalCash) || 0) + (parseFloat(totalBank) || 0);
  const variance = actualTotal - expectedTotal;

  // Check if selected date is in the future
  const today = new Date().toISOString().split('T')[0];
  const isFutureDate = selectedDate > today;

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="h-64 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Date Selection */}
      <Card>
        <CardHeader>
          <CardTitle>Daily Close</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="flex items-center space-x-4">
              <Label htmlFor="closeDate">Select Date:</Label>
              <div className="flex items-center space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={goToPreviousDay}
                  className="px-3 py-1"
                  data-testid="button-previous-day"
                >
                  <i className="fas fa-chevron-left"></i>
                </Button>
                <Input
                  id="closeDate"
                  type="date"
                  value={selectedDate}
                  max={today}
                  onChange={(e) => {
                    setSelectedDate(e.target.value);
                    // Clear form when changing dates
                    setTotalCash("");
                    setTotalBank("");
                    setNotes("");
                  }}
                  className="w-48"
                  data-testid="input-close-date"
                />
                <Button
                  variant="outline"
                  size="sm"
                  onClick={goToNextDay}
                  disabled={selectedDate >= today}
                  className="px-3 py-1"
                  data-testid="button-next-day"
                >
                  <i className="fas fa-chevron-right"></i>
                </Button>
              </div>
              {dailyCloseRecord?.isLocked ? (
                <Badge className="bg-blue-100 text-blue-800" data-testid="badge-locked">
                  <i className="fas fa-lock mr-2"></i>
                  Locked & Finalized
                </Badge>
              ) : (
                <Badge variant="outline" data-testid="badge-unlocked">
                  <i className="fas fa-edit mr-2"></i>
                  Can Edit
                </Badge>
              )}
            </div>
            <div className="text-sm text-gray-600">
              <i className="fas fa-info-circle mr-2"></i>
              You can only complete daily close operations for past and current dates. Future dates cannot be modified. Once locked, dates cannot be modified.
            </div>
            {isFutureDate && (
              <div className="bg-red-50 border border-red-200 rounded-lg p-3 text-sm text-red-700">
                <i className="fas fa-exclamation-triangle mr-2"></i>
                <strong>Future Date Selected:</strong> Daily close operations can only be performed for past and current dates. Please select a date that is today or earlier.
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Today's Summary */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <i className="fas fa-money-bill-wave text-green-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Expected Cash</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-expected-cash">
                  Rs. {expectedCash.toLocaleString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <i className="fas fa-university text-blue-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Expected Bank</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-expected-bank">
                  Rs. {expectedBank.toLocaleString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-purple-100 rounded-lg">
                <i className="fas fa-calculator text-purple-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Expected Total</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-expected-total">
                  Rs. {expectedTotal.toLocaleString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Close Form or Locked View */}
      {dailyCloseRecord?.isLocked ? (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <i className="fas fa-lock mr-2 text-blue-600"></i>
              Daily Close Record - Locked & Finalized
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-4">
              <p className="text-blue-800 text-sm font-medium">
                <i className="fas fa-info-circle mr-2"></i>
                This date has been locked and finalized. No further changes can be made.
              </p>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label>Actual Cash</Label>
                <div className="text-2xl font-semibold" data-testid="text-actual-cash">
                  Rs. {Number(dailyCloseRecord.totalCash).toLocaleString()}
                </div>
              </div>
              <div>
                <Label>Actual Bank</Label>
                <div className="text-2xl font-semibold" data-testid="text-actual-bank">
                  Rs. {Number(dailyCloseRecord.totalBank).toLocaleString()}
                </div>
              </div>
              <div>
                <Label>Variance</Label>
                <div className={`text-2xl font-semibold ${
                  Number(dailyCloseRecord.variance) === 0 ? 'text-green-600' : 
                  Number(dailyCloseRecord.variance) > 0 ? 'text-blue-600' : 'text-red-600'
                }`} data-testid="text-variance">
                  {Number(dailyCloseRecord.variance) > 0 ? '+' : ''}Rs. {Number(dailyCloseRecord.variance).toLocaleString()}
                </div>
              </div>
              <div>
                <Label>Closed At</Label>
                <div className="text-lg" data-testid="text-closed-at">
                  {new Date(dailyCloseRecord.closedAt || '').toLocaleString('en-PK')}
                </div>
              </div>
            </div>
            {dailyCloseRecord.notes && (
              <div>
                <Label>Notes</Label>
                <p className="text-gray-700 bg-gray-50 p-3 rounded border" data-testid="text-notes">{dailyCloseRecord.notes}</p>
              </div>
            )}
          </CardContent>
        </Card>
      ) : (
        <Card>
          <CardHeader>
            <CardTitle>Record Daily Close for {selectedDate}</CardTitle>
            <p className="text-sm text-gray-600">
              {new Date(selectedDate).toDateString() === new Date().toDateString() 
                ? "Recording daily close for today" 
                : "Recording daily close for a past date - you can complete missed daily closes"}
            </p>
            {isFutureDate && (
              <div className="bg-red-50 border border-red-200 rounded-lg p-4 text-center">
                <i className="fas fa-calendar-times text-red-500 text-2xl mb-2"></i>
                <h3 className="font-medium text-red-800 mb-1">Future Date Not Allowed</h3>
                <p className="text-sm text-red-600">Daily close operations can only be performed for dates up to today. Please select a current or past date.</p>
              </div>
            )}
          </CardHeader>
          <CardContent className="space-y-6">
            {!isFutureDate ? (
              <div className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <Label htmlFor="totalCash">Actual Cash Total *</Label>
                    <Input
                      id="totalCash"
                      type="number"
                      step="0.01"
                      placeholder="0.00"
                      value={totalCash}
                      onChange={(e) => setTotalCash(e.target.value)}
                      data-testid="input-total-cash"
                    />
                    <p className="text-sm text-gray-600 mt-1">
                      Expected: Rs. {expectedCash.toLocaleString()}
                    </p>
                  </div>

                  <div>
                    <Label htmlFor="totalBank">Actual Bank Total *</Label>
                    <Input
                      id="totalBank"
                      type="number"
                      step="0.01"
                      placeholder="0.00"
                      value={totalBank}
                      onChange={(e) => setTotalBank(e.target.value)}
                      data-testid="input-total-bank"
                    />
                    <p className="text-sm text-gray-600 mt-1">
                      Expected: Rs. {expectedBank.toLocaleString()}
                    </p>
                  </div>
                </div>

                {(totalCash || totalBank) && (
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <h4 className="font-medium text-gray-800 mb-2">Summary</h4>
                    <div className="grid grid-cols-3 gap-4 text-sm">
                      <div>
                        <span className="text-gray-600">Actual Total:</span>
                        <div className="font-semibold" data-testid="text-summary-actual">
                          Rs. {actualTotal.toLocaleString()}
                        </div>
                      </div>
                      <div>
                        <span className="text-gray-600">Expected:</span>
                        <div className="font-semibold" data-testid="text-summary-expected">
                          Rs. {expectedTotal.toLocaleString()}
                        </div>
                      </div>
                      <div>
                        <span className="text-gray-600">Variance:</span>
                        <div className={`font-semibold ${
                          variance === 0 ? 'text-green-600' : 
                          variance > 0 ? 'text-blue-600' : 'text-red-600'
                        }`} data-testid="text-summary-variance">
                          {variance > 0 ? '+' : ''}Rs. {variance.toLocaleString()}
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                <div>
                  <Label htmlFor="notes">Notes (Optional)</Label>
                  <Textarea
                    id="notes"
                    placeholder="Add any notes about discrepancies or special circumstances..."
                    value={notes}
                    onChange={(e) => setNotes(e.target.value)}
                    rows={3}
                    data-testid="textarea-notes"
                  />
                </div>

                <div className="flex justify-end space-x-3">
                  <Button
                    variant="outline"
                    onClick={handleSubmit}
                    disabled={(!totalCash && !totalBank) || createDailyCloseMutation.isPending}
                    data-testid="button-save-draft"
                  >
                    {createDailyCloseMutation.isPending ? (
                      <>
                        <i className="fas fa-spinner fa-spin mr-2"></i>
                        Saving...
                      </>
                    ) : (
                      "Save as Draft"
                    )}
                  </Button>
                  <Button
                    onClick={handleLockDay}
                    disabled={!totalCash || !totalBank || lockDayMutation.isPending}
                    data-testid="button-lock-day"
                    className="bg-blue-600 hover:bg-blue-700"
                  >
                    {lockDayMutation.isPending ? (
                      <>
                        <i className="fas fa-spinner fa-spin mr-2"></i>
                        Locking Day...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-lock mr-2"></i>
                        Lock Day & Finalize
                      </>
                    )}
                  </Button>
                </div>
              </div>
            ) : null}
          </CardContent>
        </Card>
      )}

      {/* Today's Transactions */}
      <Card>
        <CardHeader>
          <CardTitle>Today's Transactions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <table className="w-full text-sm">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Time</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Receipt #</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Student</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Amount</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Method</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {todaysPayments.length > 0 ? todaysPayments.map((payment: any) => (
                  <tr key={payment.id} data-testid={`row-payment-${payment.id}`}>
                    <td className="px-4 py-3" data-testid={`text-time-${payment.id}`}>
                      {new Date(payment.paymentDate).toLocaleTimeString()}
                    </td>
                    <td className="px-4 py-3" data-testid={`text-receipt-${payment.id}`}>
                      {payment.receiptNumber}
                    </td>
                    <td className="px-4 py-3" data-testid={`text-student-${payment.id}`}>
                      <div>
                        <div className="font-medium">
                          {payment.studentFirstName} {payment.studentLastName}
                        </div>
                        <div className="text-xs text-gray-500">
                          {payment.studentRollNumber}
                        </div>
                      </div>
                    </td>
                    <td className="px-4 py-3">
                      <span className="font-semibold" data-testid={`text-amount-${payment.id}`}>
                        Rs. {Number(payment.amount).toLocaleString()}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <Badge data-testid={`badge-method-${payment.id}`}>
                        {payment.paymentMethod.toUpperCase()}
                      </Badge>
                    </td>
                  </tr>
                )) : (
                  <tr>
                    <td colSpan={5} className="px-4 py-8 text-center text-gray-500">
                      No transactions for this date
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

