                      <SelectItem value="male">Male</SelectItem>
                      <SelectItem value="female">Female</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="edit-class-level">Class Level *</Label>
                  <Select 
                    value={editFormData.classLevels?.[0] || ''} 
                    onValueChange={(value) => setEditFormData(prev => ({ ...prev, classLevels: [value] }))}
                  >
                    <SelectTrigger data-testid="select-edit-class-level">
                      <SelectValue placeholder="Select class level" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="o-level">O-Level</SelectItem>
                      <SelectItem value="a-level">A-Level</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="edit-roll-number">Roll Number</Label>
                  <Input
                    id="edit-roll-number"
                    value={editFormData.rollNumber || ''}
                    disabled
                    className="bg-gray-50"
                    data-testid="input-edit-roll-number"
                  />
                  <p className="text-xs text-gray-500 mt-1">Roll number cannot be changed</p>
                </div>
                <div>
                  <Label htmlFor="edit-parent-name">Parent/Guardian Name *</Label>
                  <Input
                    id="edit-parent-name"
                    value={editFormData.parentName || ''}
                    onChange={(e) => setEditFormData(prev => ({ ...prev, parentName: e.target.value }))}
                    data-testid="input-edit-parent-name"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="edit-parent-phone">Contact Number *</Label>
                  <Input
                    id="edit-parent-phone"
                    placeholder="+92 300 1234567"
                    value={editFormData.parentPhone || ''}
                    onChange={(e) => setEditFormData(prev => ({ ...prev, parentPhone: e.target.value }))}
                    data-testid="input-edit-parent-phone"
                    required
                  />
                </div>
                <div className="col-span-2">
                  <Label htmlFor="edit-parent-email">Email Address</Label>
                  <Input
                    id="edit-parent-email"
                    type="email"
                    placeholder="parent@example.com"
                    value={editFormData.parentEmail || ''}
                    onChange={(e) => setEditFormData(prev => ({ ...prev, parentEmail: e.target.value }))}
                    data-testid="input-edit-parent-email"
                  />
                </div>
              </div>

              {/* Student Contact Information */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="edit-student-phone">Student Phone Number *</Label>
                  <Input
                    id="edit-student-phone"
                    placeholder="+92 300 1234567"
                    value={editFormData.studentPhone || ''}
                    onChange={(e) => setEditFormData(prev => ({ ...prev, studentPhone: e.target.value }))}
                    data-testid="input-edit-student-phone"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="edit-student-email">Student Email Address *</Label>
                  <Input
                    id="edit-student-email"
                    type="email"
                    placeholder="student@example.com"
                    value={editFormData.studentEmail || ''}
                    onChange={(e) => setEditFormData(prev => ({ ...prev, studentEmail: e.target.value }))}
                    data-testid="input-edit-student-email"
                    required
                  />
                </div>
                <div className="col-span-2">
                  <Label htmlFor="edit-home-address">Home Address</Label>
                  <Textarea
                    id="edit-home-address"
                    placeholder="Enter complete home address"
                    value={editFormData.homeAddress || ''}
                    onChange={(e) => setEditFormData(prev => ({ ...prev, homeAddress: e.target.value }))}
                    data-testid="input-edit-home-address"
                    rows={3}
                  />
                </div>
              </div>

              {/* Additional Parent/Guardian Information */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="edit-additional-parent-name">Additional Parent/Guardian Name</Label>
                  <Input
                    id="edit-additional-parent-name"
                    placeholder="Enter additional parent/guardian name"
                    value={editFormData.additionalParentName || ''}
                    onChange={(e) => setEditFormData(prev => ({ ...prev, additionalParentName: e.target.value }))}
                    data-testid="input-edit-additional-parent-name"
                  />
                </div>
                <div>
                  <Label htmlFor="edit-additional-parent-phone">Additional Parent/Guardian Contact Number</Label>
                  <Input
                    id="edit-additional-parent-phone"
                    placeholder="+92 300 1234567"
                    value={editFormData.additionalParentPhone || ''}
                    onChange={(e) => setEditFormData(prev => ({ ...prev, additionalParentPhone: e.target.value }))}
                    data-testid="input-edit-additional-parent-phone"
                  />
                </div>
                <div className="col-span-2">
                  <Label htmlFor="edit-additional-parent-email">Additional Parent/Guardian Email Address</Label>
                  <Input
                    id="edit-additional-parent-email"
                    type="email"
                    placeholder="additional.parent@example.com"
                    value={editFormData.additionalParentEmail || ''}
                    onChange={(e) => setEditFormData(prev => ({ ...prev, additionalParentEmail: e.target.value }))}
                    data-testid="input-edit-additional-parent-email"
                  />
                </div>
              </div>
              <div className="flex justify-end space-x-2 pt-4">
                <Button
                  variant="outline"
                  onClick={() => setShowEditDialog(false)}
                  data-testid="button-cancel-edit"
                >
                  Cancel
                </Button>
                <Button 
                  onClick={handleSaveEdit}
                  disabled={editStudentMutation.isPending}
                  data-testid="button-save-edit"
                >
                  {editStudentMutation.isPending ? "Saving..." : "Save Changes"}
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={showDeleteDialog} onOpenChange={setShowDeleteDialog}>
        <DialogContent aria-describedby="delete-student-description">
          <DialogHeader>
            <DialogTitle>Delete Student</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div className="text-center" id="delete-student-description">
              <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-red-100 mb-4">
                <Trash2 className="h-6 w-6 text-red-600" />
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Are you absolutely sure?
              </h3>
              <p className="text-sm text-gray-500 mb-4">
                You are about to permanently delete{" "}
                <strong>
                  {studentToDelete?.firstName} {studentToDelete?.lastName}
                </strong>{" "}
                from the system.
              </p>
              <div className="bg-red-50 border border-red-200 rounded-md p-3 mb-4">
                <p className="text-xs text-red-700">
                  <strong>Warning:</strong> This action cannot be undone. All student data, 
                  including grades, attendance records, financial history, and portal access 
                  will be permanently removed from the database.
                </p>
              </div>
              <p className="text-xs text-gray-600">
                If you want to temporarily disable the student instead, use the 
                "Deactivate" option which preserves all data.
              </p>
            </div>
          </div>
          <div className="flex justify-end space-x-2 pt-4">
            <Button
              variant="outline"
              onClick={() => setShowDeleteDialog(false)}
              data-testid="button-cancel-delete"
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={confirmDeleteStudent}
              disabled={deleteStudentMutation.isPending}
              data-testid="button-confirm-delete"
            >
              {deleteStudentMutation.isPending ? "Deleting..." : "Yes, Delete Permanently"}
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/management/Expenses.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Expense } from "@shared/schema";

export default function Expenses() {
  const [searchQuery, setSearchQuery] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [newExpense, setNewExpense] = useState({
    category: "",
    description: "",
    amount: "",
    expenseDate: new Date().toISOString().split('T')[0],
    paymentMethod: "cash" as const,
    whoPaid: "", // Track who paid for the expense
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: expenses, isLoading } = useQuery<Expense[]>({
    queryKey: ['/api/expenses'],
  });

  // Get all teachers and management staff for the "Who Paid" dropdown
  const { data: staffMembers } = useQuery({
    queryKey: ['/api/staff'],
  });

  const createExpenseMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest('POST', '/api/expenses', data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Expense recorded successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/expenses'] });
      setNewExpense({
        category: "",
        description: "",
        amount: "",
        expenseDate: new Date().toISOString().split('T')[0],
        paymentMethod: "cash",
        whoPaid: "",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to record expense. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmitExpense = () => {
    if (!newExpense.category || !newExpense.description || !newExpense.amount || !newExpense.whoPaid) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields including who paid.",
        variant: "destructive",
      });
      return;
    }

    const amount = parseFloat(newExpense.amount);
    if (amount <= 0) {
      toast({
        title: "Validation Error",
        description: "Please enter a valid amount.",
        variant: "destructive",
      });
      return;
    }

    createExpenseMutation.mutate({
      ...newExpense,
      amount,
    });
  };

  const filteredExpenses = expenses?.filter(expense => {
    const matchesSearch = searchQuery === "" || 
      expense.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
      expense.category.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesCategory = categoryFilter === "all" || expense.category === categoryFilter;

    return matchesSearch && matchesCategory;
  }) || [];

  const expenseCategories = [
    "Teacher Salaries",
    "Utilities",
    "Administrative",
    "Supplies",
    "Maintenance",
    "Marketing",
    "Technology",
    "Other"
  ];

  const getMethodColor = (method: string) => {
    switch (method) {
      case 'cash':
        return 'bg-green-100 text-green-800';
      case 'bank_transfer':
        return 'bg-blue-100 text-blue-800';
      case 'card':
        return 'bg-purple-100 text-purple-800';
      case 'cheque':
        return 'bg-orange-100 text-orange-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Calculate category totals
  const categoryTotals = expenses?.reduce((acc, expense) => {
    const category = expense.category;
    acc[category] = (acc[category] || 0) + Number(expense.amount);
    return acc;
  }, {} as Record<string, number>) || {};

  const totalExpenses = expenses?.reduce((sum, expense) => sum + Number(expense.amount), 0) || 0;

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="h-64 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-red-100 rounded-lg">
                <i className="fas fa-minus-circle text-red-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Expenses</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-total-expenses">
                  Rs. {totalExpenses.toLocaleString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <i className="fas fa-calendar-month text-blue-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">This Month</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-monthly-expenses">
                  Rs. {expenses?.filter(e => {
                    const expenseDate = new Date(e.expenseDate);
                    const currentMonth = new Date();
                    return expenseDate.getMonth() === currentMonth.getMonth() &&
                           expenseDate.getFullYear() === currentMonth.getFullYear();
                  }).reduce((sum, e) => sum + Number(e.amount), 0).toLocaleString() || '0'}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <i className="fas fa-list text-green-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Categories</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-categories">
                  {Object.keys(categoryTotals).length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-purple-100 rounded-lg">
                <i className="fas fa-receipt text-purple-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Transactions</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-transactions">
                  {expenses?.length || 0}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Expense Management */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Expense Management</CardTitle>
            <div className="flex space-x-3">
              <div className="relative">
                <Input
                  placeholder="Search expenses..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                  data-testid="input-search-expenses"
                />
                <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
              </div>

              <Select value={categoryFilter} onValueChange={setCategoryFilter}>
                <SelectTrigger className="w-40" data-testid="select-category-filter">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  {expenseCategories.map((category) => (
                    <SelectItem key={category} value={category}>
                      {category}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Dialog>
                <DialogTrigger asChild>
                  <Button data-testid="button-add-expense">
                    <i className="fas fa-plus mr-2"></i>
                    Add Expense
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Record New Expense</DialogTitle>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="category">Category *</Label>
                      <Select value={newExpense.category} onValueChange={(value) => setNewExpense(prev => ({ ...prev, category: value }))}>
                        <SelectTrigger data-testid="select-expense-category">
                          <SelectValue placeholder="Select category..." />
                        </SelectTrigger>
                        <SelectContent>
                          {expenseCategories.map((category) => (
                            <SelectItem key={category} value={category}>
                              {category}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label htmlFor="description">Description *</Label>
                      <Textarea
                        id="description"
                        placeholder="Describe the expense..."
                        value={newExpense.description}
                        onChange={(e) => setNewExpense(prev => ({ ...prev, description: e.target.value }))}
                        data-testid="textarea-expense-description"
                      />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="amount">Amount (Rs. ) *</Label>
                        <Input
                          id="amount"
                          type="number"
                          placeholder="0.00"
                          value={newExpense.amount}
                          onChange={(e) => setNewExpense(prev => ({ ...prev, amount: e.target.value }))}
                          data-testid="input-expense-amount"
                        />
                      </div>

                      <div>
                        <Label htmlFor="expenseDate">Date *</Label>
                        <Input
                          id="expenseDate"
                          type="date"
                          value={newExpense.expenseDate}
                          onChange={(e) => setNewExpense(prev => ({ ...prev, expenseDate: e.target.value }))}
                          data-testid="input-expense-date"
                        />
                      </div>
                    </div>

                    <div>
                      <Label htmlFor="paymentMethod">Payment Method *</Label>
                      <Select value={newExpense.paymentMethod} onValueChange={(value) => setNewExpense(prev => ({ ...prev, paymentMethod: value as any }))}>
                        <SelectTrigger data-testid="select-payment-method">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="cash">Cash</SelectItem>
                          <SelectItem value="bank_transfer">Bank Transfer</SelectItem>
                          <SelectItem value="card">Card</SelectItem>
                          <SelectItem value="cheque">Cheque</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label htmlFor="whoPaid">Who Paid? *</Label>
                      <Select value={newExpense.whoPaid} onValueChange={(value) => setNewExpense(prev => ({ ...prev, whoPaid: value }))}>
                        <SelectTrigger data-testid="select-who-paid">
                          <SelectValue placeholder="Select staff member..." />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="school">School/Office (No Individual)</SelectItem>
                          {staffMembers?.map((staff: any) => (
                            <SelectItem key={staff.id} value={staff.id}>
                              {staff.firstName} {staff.lastName} - {staff.role}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <p className="text-xs text-gray-500 mt-1">
                        Select who actually paid for this expense. If a teacher/staff paid out of pocket, select them for later reconciliation.
                      </p>
                    </div>

                    <div className="flex justify-end space-x-2">
                      <Button variant="outline">Cancel</Button>
                      <Button 
                        onClick={handleSubmitExpense}
                        disabled={createExpenseMutation.isPending}
                        data-testid="button-save-expense"
                      >
                        {createExpenseMutation.isPending ? 'Saving...' : 'Save Expense'}
                      </Button>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>
            </div>
          </div>
        </CardHeader>

        <CardContent>
          <div className="overflow-x-auto">
            <table className="w-full text-sm">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Date</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Category</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Description</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Amount</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Method</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Actions</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {filteredExpenses.length > 0 ? filteredExpenses.map((expense) => (
                  <tr key={expense.id} className="hover:bg-gray-50" data-testid={`row-expense-${expense.id}`}>
                    <td className="px-4 py-3" data-testid={`text-date-${expense.id}`}>
                      {new Date(expense.expenseDate).toLocaleDateString()}
                    </td>
                    <td className="px-4 py-3">
                      <Badge variant="outline" data-testid={`badge-category-${expense.id}`}>
                        {expense.category}
                      </Badge>
                    </td>
                    <td className="px-4 py-3">
                      <span className="text-gray-700 max-w-xs truncate" data-testid={`text-description-${expense.id}`}>
                        {expense.description}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <span className="font-semibold text-red-600" data-testid={`text-amount-${expense.id}`}>
                        Rs. {Number(expense.amount).toLocaleString()}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <Badge className={getMethodColor(expense.paymentMethod)} data-testid={`badge-method-${expense.id}`}>
                        {expense.paymentMethod.replace('_', ' ').toUpperCase()}
                      </Badge>
                    </td>
                    <td className="px-4 py-3">
                      <div className="flex space-x-2">
                        <Button size="sm" variant="ghost" data-testid={`button-edit-${expense.id}`}>
                          <i className="fas fa-edit"></i>
                        </Button>
                        <Button size="sm" variant="ghost" className="text-red-600" data-testid={`button-delete-${expense.id}`}>
                          <i className="fas fa-trash"></i>
                        </Button>
                      </div>
                    </td>
                  </tr>
                )) : (
                  <tr>
                    <td colSpan={6} className="px-4 py-8 text-center text-gray-500">
                      <i className="fas fa-receipt text-4xl mb-4"></i>
                      <p>No expenses found</p>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>

      {/* Category Breakdown */}
      <Card>
        <CardHeader>
          <CardTitle>Category Breakdown</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {Object.entries(categoryTotals).map(([category, amount]) => {
              const percentage = totalExpenses > 0 ? (amount / totalExpenses) * 100 : 0;
              return (
                <div key={category} className="space-y-2" data-testid={`category-${category.replace(/\s+/g, '-').toLowerCase()}`}>
                  <div className="flex justify-between items-center">
                    <span className="text-sm font-medium">{category}</span>
                    <div className="text-right">
                      <span className="text-sm font-semibold">Rs. {amount.toLocaleString()}</span>
                      <span className="text-xs text-gray-500 ml-2">({percentage.toFixed(1)}%)</span>
                    </div>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className="bg-blue-600 h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${percentage}%` }}
                    ></div>
                  </div>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/management/PayoutSummar.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface TeacherPayout {
  id: string;
  teacherId: string;
  teacherName: string;
  baseAmount: number;
  extraAmount: number;
  totalAmount: number;
  payoutRate: number;
  isFixed: boolean;
  isPaidOut: boolean;
  month: string;
  year: number;
}

export default function PayoutSummary() {
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth());
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [selectedTeacher, setSelectedTeacher] = useState("");
  const [newRate, setNewRate] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // In real implementation, these would come from actual API calls
  const { data: teachers } = useQuery({
    queryKey: ['/api/teachers'],
  });

  const { data: payoutRules } = useQuery({
    queryKey: ['/api/payout-rules'],
  });

  // Mock payout data for demonstration
  const mockPayouts: TeacherPayout[] = [
    {
      id: '1',
      teacherId: 'teacher1',
      teacherName: 'Mr. Tariq Ahmed',
      baseAmount: 21000,
      extraAmount: 3500,
      totalAmount: 24500,
      payoutRate: 70,
      isFixed: true,
      isPaidOut: false,
      month: 'March',
      year: 2024,
    },
    {
      id: '2',
      teacherId: 'teacher2',
      teacherName: 'Ms. Ayesha Malik',
      baseAmount: 18000,
      extraAmount: 2000,
      totalAmount: 20000,
      payoutRate: 65,
      isFixed: false,
      isPaidOut: true,
      month: 'March',
      year: 2024,
    },
    {
      id: '3',
      teacherId: 'teacher3',
      teacherName: 'Mr. Hassan Ali',
      baseAmount: 15000,
      extraAmount: 1500,
      totalAmount: 16500,
      payoutRate: 70,
      isFixed: true,
      isPaidOut: false,
      month: 'March',
      year: 2024,
    },
  ];

  const updatePayoutRateMutation = useMutation({
    mutationFn: async ({ teacherId, rate }: { teacherId: string; rate: number }) => {
      // In real implementation, this would update the payout rule
      return await apiRequest('PATCH', `/api/payout-rules/${teacherId}`, {
        fixedPercentage: rate,
        effectiveFrom: new Date().toISOString().split('T')[0],
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Payout rate updated successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/payout-rules'] });
      setNewRate("");
      setSelectedTeacher("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update payout rate. Please try again.",
        variant: "destructive",
      });
    },
  });

  const markAsPaidMutation = useMutation({
    mutationFn: async (payoutId: string) => {
      // In real implementation, this would mark the payout as completed
      console.log('Marking payout as paid:', payoutId);
      return Promise.resolve();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Payout marked as completed!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update payout status. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleUpdateRate = () => {
    if (!selectedTeacher || !newRate) {
      toast({
        title: "Validation Error",
        description: "Please select a teacher and enter a rate.",
        variant: "destructive",
      });
      return;
    }

    const rate = parseFloat(newRate);
    if (rate <= 0 || rate > 100) {
      toast({
        title: "Validation Error",
        description: "Please enter a valid rate between 1-100%.",
        variant: "destructive",
      });
      return;
    }

    updatePayoutRateMutation.mutate({
      teacherId: selectedTeacher,
      rate,
    });
  };

  const monthNames = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  const filteredPayouts = mockPayouts.filter(payout => 
    payout.month === monthNames[selectedMonth] && payout.year === selectedYear
  );

  const totalPayouts = filteredPayouts.reduce((sum, payout) => sum + payout.totalAmount, 0);
  const pendingPayouts = filteredPayouts.filter(p => !p.isPaidOut).length;
  const completedPayouts = filteredPayouts.filter(p => p.isPaidOut).length;

  return (
    <div className="space-y-6">
      {/* Header Controls */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Teacher Payout Summary</CardTitle>
            <div className="flex space-x-3">
              <Select value={selectedMonth.toString()} onValueChange={(value) => setSelectedMonth(parseInt(value))}>
                <SelectTrigger className="w-32" data-testid="select-month">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {monthNames.map((month, index) => (
                    <SelectItem key={index} value={index.toString()}>
                      {month}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={selectedYear.toString()} onValueChange={(value) => setSelectedYear(parseInt(value))}>
                <SelectTrigger className="w-20" data-testid="select-year">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {[2023, 2024, 2025].map((year) => (
                    <SelectItem key={year} value={year.toString()}>
                      {year}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Dialog>
                <DialogTrigger asChild>
                  <Button variant="outline" data-testid="button-manage-rates">
                    <i className="fas fa-cog mr-2"></i>
                    Manage Rates
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Update Payout Rates</DialogTitle>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="teacher">Select Teacher</Label>
                      <Select value={selectedTeacher} onValueChange={setSelectedTeacher}>
                        <SelectTrigger data-testid="select-teacher-rate">
                          <SelectValue placeholder="Choose teacher..." />
                        </SelectTrigger>
                        <SelectContent>
                          {filteredPayouts.map((payout) => (
                            <SelectItem key={payout.teacherId} value={payout.teacherId}>
                              {payout.teacherName}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label htmlFor="rate">New Payout Rate (%)</Label>
                      <Input
                        id="rate"
                        type="number"
                        min="1"
                        max="100"
                        placeholder="70"
                        value={newRate}
                        onChange={(e) => setNewRate(e.target.value)}
                        data-testid="input-new-rate"
                      />
                    </div>

                    <div className="flex justify-end space-x-2">
                      <Button variant="outline">Cancel</Button>
                      <Button 
                        onClick={handleUpdateRate}
                        disabled={updatePayoutRateMutation.isPending}
                        data-testid="button-update-rate"
                      >
                        {updatePayoutRateMutation.isPending ? 'Updating...' : 'Update Rate'}
                      </Button>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Summary Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <i className="fas fa-dollar-sign text-blue-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Payouts</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-total-payouts">
                  Rs. {totalPayouts.toLocaleString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-yellow-100 rounded-lg">
                <i className="fas fa-clock text-yellow-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-pending-payouts">
                  {pendingPayouts}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <i className="fas fa-check-circle text-green-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Completed</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-completed-payouts">
                  {completedPayouts}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-purple-100 rounded-lg">
                <i className="fas fa-users text-purple-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Teachers</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-total-teachers">
                  {filteredPayouts.length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Payout Table */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>
              {monthNames[selectedMonth]} {selectedYear} Payouts
            </CardTitle>
            <div className="flex space-x-2">
              <Button variant="outline" data-testid="button-export-payouts">
                <i className="fas fa-download mr-2"></i>
                Export
              </Button>
              <Button 
                disabled={pendingPayouts === 0}
                data-testid="button-process-all"
              >
                <i className="fas fa-credit-card mr-2"></i>
                Process All Pending
              </Button>
            </div>
          </div>
        </CardHeader>

        <CardContent>
          <div className="overflow-x-auto">
            <table className="w-full text-sm">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Teacher</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Rate</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Base Amount</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Extra Amount</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Total Amount</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Status</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Actions</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {filteredPayouts.length > 0 ? filteredPayouts.map((payout) => (
                  <tr key={payout.id} className="hover:bg-gray-50" data-testid={`row-payout-${payout.id}`}>
                    <td className="px-4 py-3">
                      <div className="flex items-center">
                        <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center mr-3">
                          <i className="fas fa-user-tie text-blue-600 text-sm"></i>
                        </div>
                        <div>
                          <p className="font-medium text-gray-900" data-testid={`teacher-name-${payout.id}`}>
                            {payout.teacherName}
                          </p>
                          <p className="text-xs text-gray-500">
                            {payout.isFixed ? 'Fixed Rate' : 'Tiered Rate'}
                          </p>
                        </div>
                      </div>
                    </td>
                    <td className="px-4 py-3">
                      <Badge variant="outline" data-testid={`rate-${payout.id}`}>
                        {payout.payoutRate}%
                      </Badge>
                    </td>
                    <td className="px-4 py-3">
                      <span className="font-semibold" data-testid={`base-amount-${payout.id}`}>
                        Rs. {payout.baseAmount.toLocaleString()}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <span className="font-semibold text-green-600" data-testid={`extra-amount-${payout.id}`}>
                        Rs. {payout.extraAmount.toLocaleString()}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <span className="font-bold text-blue-600" data-testid={`total-amount-${payout.id}`}>
                        Rs. {payout.totalAmount.toLocaleString()}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <Badge 
                        className={payout.isPaidOut ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}
                        data-testid={`status-${payout.id}`}
                      >
                        <i className={`fas fa-${payout.isPaidOut ? 'check-circle' : 'clock'} mr-1`}></i>
                        {payout.isPaidOut ? 'Paid' : 'Pending'}
                      </Badge>
                    </td>
                    <td className="px-4 py-3">
                      <div className="flex space-x-2">
                        <Button size="sm" variant="ghost" data-testid={`button-view-details-${payout.id}`}>
                          <i className="fas fa-eye"></i>
                        </Button>
                        {!payout.isPaidOut && (
                          <Button 
                            size="sm" 
                            className="bg-green-600 hover:bg-green-700"
                            onClick={() => markAsPaidMutation.mutate(payout.id)}
                            disabled={markAsPaidMutation.isPending}
                            data-testid={`button-mark-paid-${payout.id}`}
                          >
                            <i className="fas fa-check mr-1"></i>
                            Mark Paid
                          </Button>
                        )}
                      </div>
                    </td>
                  </tr>
                )) : (
                  <tr>
                    <td colSpan={7} className="px-4 py-8 text-center text-gray-500">
                      <i className="fas fa-users text-4xl mb-4"></i>
                      <p>No payout data for selected period</p>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>

      {/* Payout Rules */}
      <Card>
        <CardHeader>
          <CardTitle>Current Payout Rules</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-4">
              <h4 className="font-medium text-gray-800">Fixed Rate Teachers</h4>
              {filteredPayouts.filter(p => p.isFixed).map((teacher) => (
                <div key={teacher.id} className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
                  <span className="font-medium">{teacher.teacherName}</span>
                  <Badge className="bg-blue-100 text-blue-800">{teacher.payoutRate}%</Badge>
                </div>
              ))}
            </div>

            <div className="space-y-4">
              <h4 className="font-medium text-gray-800">Tiered Rate Teachers</h4>
              {filteredPayouts.filter(p => !p.isFixed).map((teacher) => (
                <div key={teacher.id} className="flex items-center justify-between p-3 bg-purple-50 rounded-lg">
                  <span className="font-medium">{teacher.teacherName}</span>
                  <div className="text-right">
                    <Badge className="bg-purple-100 text-purple-800">{teacher.payoutRate}%</Badge>
                    <p className="text-xs text-gray-500 mt-1">Base rate</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

