import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import type { Attendance, Student } from "@shared/schema";

export default function AttendanceCalendar() {
  const [selectedChild, setSelectedChild] = useState("");
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth());
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());

  const { data: children } = useQuery<Student[]>({
    queryKey: ['/api/students'], // In real implementation, filter by parent ID
  });

  const { data: attendanceData } = useQuery<Attendance[]>({
    queryKey: ['/api/attendance/student', selectedChild],
    enabled: !!selectedChild,
  });

  const selectedChildData = children?.find(child => child.id === selectedChild);

  // Generate calendar days for the selected month
  const generateCalendarDays = () => {
    const firstDay = new Date(selectedYear, selectedMonth, 1);
    const lastDay = new Date(selectedYear, selectedMonth + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();

    const days = [];

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }

    // Add days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(day);
    }

    return days;
  };

  const getAttendanceStatus = (day: number) => {
    if (!attendanceData || !day) return null;

    const dateString = new Date(selectedYear, selectedMonth, day).toISOString().split('T')[0];
    const attendance = attendanceData.find(a => 
      a.attendanceDate === dateString
    );

    return attendance?.status || null;
  };

  const getStatusColor = (status: string | null) => {
    switch (status) {
      case 'present':
        return 'bg-green-500 text-white';
      case 'absent':
        return 'bg-red-500 text-white';
      case 'late':
        return 'bg-yellow-500 text-white';
      default:
        // Weekend or holiday
        const dayOfWeek = new Date(selectedYear, selectedMonth, parseInt(status || '1')).getDay();
        return dayOfWeek === 0 || dayOfWeek === 6 ? 'bg-gray-200 text-gray-600' : 'bg-gray-100 text-gray-800';
    }
  };

  const isToday = (day: number) => {
    const today = new Date();
    return today.getDate() === day && 
           today.getMonth() === selectedMonth && 
           today.getFullYear() === selectedYear;
  };

  const monthNames = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  const calendarDays = generateCalendarDays();
  const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  // Calculate statistics
  const thisMonthAttendance = attendanceData?.filter(a => {
    const attendanceDate = new Date(a.attendanceDate);
    return attendanceDate.getMonth() === selectedMonth && 
           attendanceDate.getFullYear() === selectedYear;
  }) || [];

  const presentCount = thisMonthAttendance.filter(a => a.status === 'present').length;
  const absentCount = thisMonthAttendance.filter(a => a.status === 'absent').length;
  const lateCount = thisMonthAttendance.filter(a => a.status === 'late').length;
  const totalDays = thisMonthAttendance.length;
  const attendancePercentage = totalDays > 0 ? (presentCount / totalDays) * 100 : 0;

  return (
    <div className="space-y-6">
      {/* Header Controls */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Attendance Calendar</CardTitle>
            <div className="flex space-x-3">
              {children && children.length > 1 && (
                <Select value={selectedChild} onValueChange={setSelectedChild}>
                  <SelectTrigger className="w-48" data-testid="select-child">
                    <SelectValue placeholder="Select child..." />
                  </SelectTrigger>
                  <SelectContent>
                    {children.map((child) => (
                      <SelectItem key={child.id} value={child.id}>
                        {child.firstName} {child.lastName}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              )}

              <Select value={selectedMonth.toString()} onValueChange={(value) => setSelectedMonth(parseInt(value))}>
                <SelectTrigger className="w-32" data-testid="select-month">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {monthNames.map((month, index) => (
                    <SelectItem key={index} value={index.toString()}>
                      {month}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={selectedYear.toString()} onValueChange={(value) => setSelectedYear(parseInt(value))}>
                <SelectTrigger className="w-20" data-testid="select-year">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {[2023, 2024, 2025].map((year) => (
                    <SelectItem key={year} value={year.toString()}>
                      {year}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardHeader>
      </Card>

      {!selectedChild && children && children.length > 0 && (
        <Card>
          <CardContent className="pt-6">
            <div className="text-center py-12">
              <i className="fas fa-calendar-alt text-4xl text-gray-400 mb-4"></i>
              <h3 className="text-lg font-medium text-gray-900 mb-2">Select a Child</h3>
              <p className="text-gray-600">
                Choose a child from the dropdown above to view their attendance calendar
              </p>
            </div>
          </CardContent>
        </Card>
      )}

      {selectedChild && (
        <>
          {/* Attendance Statistics */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <Card>
              <CardContent className="pt-6">
                <div className="text-center">
                  <p className="text-2xl font-bold text-green-600" data-testid="stat-present">
                    {presentCount}
                  </p>
                  <p className="text-sm text-gray-600">Present</p>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="text-center">
                  <p className="text-2xl font-bold text-red-600" data-testid="stat-absent">
                    {absentCount}
                  </p>
                  <p className="text-sm text-gray-600">Absent</p>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="text-center">
                  <p className="text-2xl font-bold text-yellow-600" data-testid="stat-late">
                    {lateCount}
                  </p>
                  <p className="text-sm text-gray-600">Late</p>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="pt-6">
                <div className="text-center">
                  <p className="text-2xl font-bold text-blue-600" data-testid="stat-percentage">
                    {attendancePercentage.toFixed(0)}%
                  </p>
                  <p className="text-sm text-gray-600">Attendance</p>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Calendar */}
          <Card>
            <CardHeader>
              <CardTitle>
                {monthNames[selectedMonth]} {selectedYear} - {selectedChildData?.firstName} {selectedChildData?.lastName}
              </CardTitle>
            </CardHeader>
            <CardContent>
              {/* Calendar Header */}
              <div className="grid grid-cols-7 gap-2 mb-4">
                {daysOfWeek.map((day) => (
                  <div key={day} className="p-2 text-center text-xs font-medium text-gray-600">
                    {day}
                  </div>
                ))}
              </div>

              {/* Calendar Days */}
              <div className="grid grid-cols-7 gap-2">
                {calendarDays.map((day, index) => {
                  if (day === null) {
                    return <div key={index} className="p-2"></div>;
                  }

                  const status = getAttendanceStatus(day);
                  const todayClass = isToday(day) ? 'ring-2 ring-blue-500' : '';

                  return (
                    <div
                      key={day}
                      className={`
                        w-10 h-10 rounded-full flex items-center justify-center text-sm font-medium
                        ${getStatusColor(status)} ${todayClass}
                      `}
                      data-testid={`calendar-day-${day}`}
                    >
                      {day}
                    </div>
                  );
                })}
              </div>

              {/* Legend */}
              <div className="flex items-center justify-center space-x-6 mt-6 text-xs">
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-green-500 rounded-full mr-2"></div>
                  <span className="text-gray-600">Present</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-red-500 rounded-full mr-2"></div>
                  <span className="text-gray-600">Absent</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-yellow-500 rounded-full mr-2"></div>
                  <span className="text-gray-600">Late</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-blue-500 rounded-full mr-2"></div>
                  <span className="text-gray-600">Today</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-gray-200 rounded-full mr-2"></div>
                  <span className="text-gray-600">Holiday</span>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Subject-wise Attendance */}
          <Card>
            <CardHeader>
              <CardTitle>Subject-wise Attendance</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                  <div className="flex items-center">
                    <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center mr-3">
                      <i className="fas fa-flask text-blue-600 text-sm"></i>
                    </div>
                    <span className="font-medium">Chemistry</span>
                  </div>
                  <div className="text-right">
                    <p className="font-semibold text-green-600" data-testid="chemistry-percentage">96%</p>
                    <p className="text-xs text-gray-500">24/25 classes</p>
                  </div>
                </div>

                <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                  <div className="flex items-center">
                    <div className="w-8 h-8 bg-green-100 rounded-full flex items-center justify-center mr-3">
                      <i className="fas fa-calculator text-green-600 text-sm"></i>
                    </div>
                    <span className="font-medium">Physics</span>
                  </div>
                  <div className="text-right">
                    <p className="font-semibold text-green-600" data-testid="physics-percentage">92%</p>
                    <p className="text-xs text-gray-500">23/25 classes</p>
                  </div>
                </div>

                <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                  <div className="flex items-center">
                    <div className="w-8 h-8 bg-purple-100 rounded-full flex items-center justify-center mr-3">
                      <i className="fas fa-book text-purple-600 text-sm"></i>
                    </div>
                    <span className="font-medium">Mathematics</span>
                  </div>
                  <div className="text-right">
                    <p className="font-semibold text-yellow-600" data-testid="math-percentage">88%</p>
                    <p className="text-xs text-gray-500">22/25 classes</p>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </>
      )}
    </div>
  );
}

client/src/pages/teacher/Attendance.tsx.content:
import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Check, X, Clock, QrCode, Users, Calendar } from "lucide-react";
import QrScanner from "qr-scanner";

interface Student {
  id: string;
  firstName: string;
  lastName: string;
  rollNumber: string;
  profileImageUrl?: string;
}

interface AttendanceRecord {
  studentId: string;
  status: 'present' | 'absent' | 'late';
}

export default function Attendance() {
  const [selectedClass, setSelectedClass] = useState("");
  const [attendanceRecords, setAttendanceRecords] = useState<AttendanceRecord[]>([]);
  const [attendanceMethod, setAttendanceMethod] = useState<'tap' | 'qr'>('tap');
  const [isScanning, setIsScanning] = useState(false);
  const [qrScanner, setQrScanner] = useState<QrScanner | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const videoRef = useRef<HTMLVideoElement>(null);

  const { data: todayClasses } = useQuery({
    queryKey: ['/api/teacher/classes/today'],
  });

  const { data: students, isLoading: studentsLoading } = useQuery<Student[]>({
    queryKey: ['/api/classes', selectedClass, 'students'],
    enabled: !!selectedClass,
  });

  const submitAttendanceMutation = useMutation({
    mutationFn: async () => {
      const promises = attendanceRecords.map(record => 
        apiRequest('POST', '/api/attendance', {
          classId: selectedClass,
          studentId: record.studentId,
          attendanceDate: new Date().toISOString().split('T')[0],
          status: record.status,
        })
      );
      return Promise.all(promises);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Attendance submitted successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      setAttendanceRecords([]);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit attendance. Please try again.",
        variant: "destructive",
      });
    },
  });

  const markAttendance = (studentId: string, status: 'present' | 'absent' | 'late') => {
    setAttendanceRecords(prev => {
      const existing = prev.find(r => r.studentId === studentId);
      if (existing) {
        return prev.map(r => r.studentId === studentId ? { ...r, status } : r);
      }
      return [...prev, { studentId, status }];
    });
  };

  const undoMark = (studentId: string) => {
    setAttendanceRecords(prev => prev.filter(r => r.studentId !== studentId));
  };

  const getStudentStatus = (studentId: string): 'present' | 'absent' | 'late' | undefined => {
    return attendanceRecords.find(r => r.studentId === studentId)?.status;
  };

  const handleSubmit = () => {
    if (attendanceRecords.length === 0) {
      toast({
        title: "Warning",
        description: "Please mark attendance for at least one student.",
        variant: "destructive",
      });
      return;
    }
    submitAttendanceMutation.mutate();
  };

  // QR Scanner setup
  useEffect(() => {
    if (attendanceMethod === 'qr' && videoRef.current && !qrScanner && isScanning) {
      const scanner = new QrScanner(
        videoRef.current,
        (result: QrScanner.ScanResult) => handleQrScan(result.data),
        {
          preferredCamera: 'environment',
          highlightScanRegion: true,
          highlightCodeOutline: true,
        }
      );
      setQrScanner(scanner);
      scanner.start();
    }

    return () => {
      if (qrScanner) {
        qrScanner.stop();
        qrScanner.destroy();
        setQrScanner(null);
      }
    };
  }, [attendanceMethod, isScanning]);

  const handleQrScan = (qrData: string) => {
    try {
      // Expected QR format: studentId:rollNumber or just studentId
      const studentId = qrData.includes(':') ? qrData.split(':')[0] : qrData;
      const student = students?.find(s => s.id === studentId || s.rollNumber === qrData);

      if (student) {
        // Auto-mark as present when QR is scanned
        markAttendance(student.id, 'present');
        toast({
          title: "Student Scanned",
          description: `${student.firstName} ${student.lastName} marked as present`,
        });
      } else {
        toast({
          title: "Student Not Found",
          description: "This QR code doesn't match any student in the current class",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Invalid QR Code",
        description: "Unable to read student information from this QR code",
        variant: "destructive",
      });
    }
  };

  const toggleScanning = () => {
    if (isScanning && qrScanner) {
      qrScanner.stop();
      setIsScanning(false);
    } else {
      setIsScanning(true);
    }
  };

  const presentCount = attendanceRecords.filter(r => r.status === 'present').length;
  const absentCount = attendanceRecords.filter(r => r.status === 'absent').length;
  const lateCount = attendanceRecords.filter(r => r.status === 'late').length;
  const totalStudents = students?.length || 0;
  const unmarkedCount = totalStudents - attendanceRecords.length;

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center gap-2">
                <Users className="h-5 w-5" />
                Take Attendance
              </CardTitle>
              {selectedClass && (
                <p className="text-gray-600 mt-1 flex items-center gap-2" data-testid="text-class-info">
                  <Calendar className="h-4 w-4" />
                  {todayClasses?.find((c: any) => c.id === selectedClass)?.subject} - {new Date().toLocaleDateString()}
                </p>
              )}
            </div>
            {selectedClass && (
              <div className="grid grid-cols-4 gap-4 text-center">
                <div>
                  <div className="flex items-center justify-center gap-1">
                    <Check className="h-4 w-4 text-green-600" />
                    <span className="text-sm text-gray-600">Present</span>
                  </div>
                  <p className="text-2xl font-bold text-green-600" data-testid="text-present-count">
                    {presentCount}
                  </p>
                </div>
                <div>
                  <div className="flex items-center justify-center gap-1">
                    <X className="h-4 w-4 text-red-600" />
                    <span className="text-sm text-gray-600">Absent</span>
                  </div>
                  <p className="text-2xl font-bold text-red-600" data-testid="text-absent-count">
                    {absentCount}
                  </p>
                </div>
                <div>
                  <div className="flex items-center justify-center gap-1">
                    <Clock className="h-4 w-4 text-yellow-600" />
                    <span className="text-sm text-gray-600">Late</span>
                  </div>
                  <p className="text-2xl font-bold text-yellow-600" data-testid="text-late-count">
                    {lateCount}
                  </p>
                </div>
                <div>
                  <div className="flex items-center justify-center gap-1">
                    <Users className="h-4 w-4 text-gray-600" />
                    <span className="text-sm text-gray-600">Unmarked</span>
                  </div>
                  <p className="text-2xl font-bold text-gray-600" data-testid="text-unmarked-count">
                    {unmarkedCount}
                  </p>
                </div>
              </div>
            )}
          </div>
        </CardHeader>

        <CardContent className="space-y-4">
          {/* Class Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Select Class</label>
            <Select value={selectedClass} onValueChange={setSelectedClass}>
              <SelectTrigger data-testid="select-class">
                <SelectValue placeholder="Choose a class..." />
              </SelectTrigger>
              <SelectContent>
                {todayClasses?.map((classItem: any) => (
                  <SelectItem key={classItem.id} value={classItem.id}>
                    {classItem.subject} ({classItem.startTime} - {classItem.endTime})
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Method Toggle */}
          {selectedClass && (
            <Tabs value={attendanceMethod} onValueChange={(value) => setAttendanceMethod(value as 'tap' | 'qr')} className="w-full">
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="tap" className="flex items-center gap-2" data-testid="tab-tap-method">
                  <Users className="h-4 w-4" />
                  Tap Interface
                </TabsTrigger>
                <TabsTrigger value="qr" className="flex items-center gap-2" data-testid="tab-qr-method">
                  <QrCode className="h-4 w-4" />
                  QR Scanner
                </TabsTrigger>
              </TabsList>

              <TabsContent value="tap" className="mt-6">
                {studentsLoading ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {[1, 2, 3, 4, 5, 6].map((i) => (
                      <div key={i} className="animate-pulse bg-gray-200 h-20 rounded-lg"></div>
                    ))}
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {students?.map((student) => {
                      const status = getStudentStatus(student.id);
                      const isMarked = !!status;

                      return (
                        <Card 
                          key={student.id} 
                          className={`${
                            status === 'present' ? 'bg-green-50 border-green-200' :
                            status === 'absent' ? 'bg-red-50 border-red-200' :
                            status === 'late' ? 'bg-yellow-50 border-yellow-200' :
                            'border-gray-200'
                          }`}
                          data-testid={`card-student-${student.id}`}
                        >
                          <CardContent className="pt-4">
                            <div className="flex items-center justify-between">
                              <div className="flex items-center">
                                <div className="w-10 h-10 bg-gray-300 rounded-full mr-3 flex items-center justify-center text-xs font-bold text-gray-700">
                                  {student.rollNumber}
                                </div>
                                <div>
                                  <p className="text-sm font-medium text-gray-900" data-testid={`text-student-name-${student.id}`}>
                                    {student.firstName} {student.lastName}
                                  </p>
                                  <p className="text-xs text-gray-500" data-testid={`text-roll-number-${student.id}`}>
                                    Roll: {student.rollNumber}
                                  </p>
                                </div>
                              </div>

                              {isMarked ? (
                                <div className="flex items-center space-x-1">
                                  <Badge 
                                    className={
                                      status === 'present' ? 'bg-green-100 text-green-800' :
                                      status === 'absent' ? 'bg-red-100 text-red-800' :
                                      'bg-yellow-100 text-yellow-800'
                                    }
                                    data-testid={`badge-status-${student.id}`}
                                  >
                                    {status}
                                  </Badge>
                                  <Button
                                    size="sm"
                                    variant="ghost"
                                    onClick={() => undoMark(student.id)}
                                    className="w-6 h-6 p-0"
                                    data-testid={`button-undo-${student.id}`}
                                  >
                                    <i className="fas fa-undo text-xs"></i>
                                  </Button>
                                </div>
                              ) : (
                                <div className="flex space-x-1">
                                  <Button
                                    size="sm"
                                    className="w-8 h-8 p-0 bg-green-600 hover:bg-green-700"
                                    onClick={() => markAttendance(student.id, 'present')}
                                    data-testid={`button-present-${student.id}`}
                                  >
                                    <i className="fas fa-check text-xs"></i>
                                  </Button>
                                  <Button
                                    size="sm"
                                    className="w-8 h-8 p-0 bg-red-600 hover:bg-red-700"
                                    onClick={() => markAttendance(student.id, 'absent')}
                                    data-testid={`button-absent-${student.id}`}
                                  >
                                    <i className="fas fa-times text-xs"></i>
                                  </Button>
                                  <Button
                                    size="sm"
                                    className="w-8 h-8 p-0 bg-yellow-600 hover:bg-yellow-700"
                                    onClick={() => markAttendance(student.id, 'late')}
                                    data-testid={`button-late-${student.id}`}
                                  >
                                    <i className="fas fa-clock text-xs"></i>
                                  </Button>
                                </div>
                              )}
                            </div>
                          </CardContent>
                        </Card>
                      );
                    })}
                  </div>
                )}
              </TabsContent>

              <TabsContent value="qr" className="mt-6">
                <Card>
                  <CardContent className="p-6">
                    <div className="text-center mb-4">
                      <QrCode className="h-12 w-12 text-gray-400 mx-auto mb-2" />
                      <h3 className="text-lg font-medium text-gray-900 mb-2">QR Code Scanner</h3>
                      <p className="text-gray-600 mb-4">
                        Scan student QR codes to automatically mark attendance as present
                      </p>
                    </div>

                    <div className="space-y-4">
                      <div className="flex justify-center">
                        <Button 
                          onClick={toggleScanning}
                          data-testid="button-toggle-scanner"
                          className={isScanning ? "bg-red-600 hover:bg-red-700" : "bg-green-600 hover:bg-green-700"}
                        >
                          {isScanning ? (
                            <>
                              <X className="h-4 w-4 mr-2" />
                              Stop Scanner
                            </>
                          ) : (
                            <>
                              <QrCode className="h-4 w-4 mr-2" />
                              Start Scanner
                            </>
                          )}
                        </Button>
                      </div>

                      {isScanning && (
                        <div className="relative">
                          <video 
                            ref={videoRef}
                            className="w-full max-w-md mx-auto rounded-lg border-2 border-dashed border-gray-300"
                            style={{ aspectRatio: '1/1' }}
                          />
                          <div className="text-center mt-2">
                            <p className="text-sm text-gray-600">
                              Hold student QR codes within the camera view
                            </p>
                          </div>
                        </div>
                      )}

                      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                        <h4 className="font-medium text-blue-900 mb-2">QR Code Format</h4>
                        <p className="text-sm text-blue-700">
                          Expected format: <code className="bg-blue-100 px-1 rounded">studentId:rollNumber</code> or just <code className="bg-blue-100 px-1 rounded">studentId</code>
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>
            </Tabs>
          )}
        </CardContent>
      </Card>

      {/* Action Buttons */}
      {selectedClass && students && students.length > 0 && (
        <Card>
          <CardContent className="pt-6">
            <div className="flex justify-between items-center">
              <div className="text-sm text-gray-600" data-testid="text-progress">
                <span>{attendanceRecords.length}</span> of <span>{totalStudents}</span> students marked
              </div>
              <div className="flex space-x-3">
                <Button 
                  variant="outline"
                  disabled={attendanceRecords.length === 0}
                  data-testid="button-save-draft"
                >
                  Save as Draft
                </Button>
                <Button 
                  onClick={handleSubmit}
                  disabled={attendanceRecords.length === 0 || submitAttendanceMutation.isPending}
                  data-testid="button-submit-attendance"
                >
                  {submitAttendanceMutation.isPending ? 'Submitting...' : 'Submit Attendance'}
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/teacher/Dashboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Link } from "wouter";
import type { ClassSchedule } from "@/types";

export default function TeacherDashboard() {
  const { data: todayClasses, isLoading: classesLoading } = useQuery<ClassSchedule[]>({
    queryKey: ['/api/teacher/classes/today'],
  });



  const currentTime = new Date();
  const currentHour = currentTime.getHours();
  const currentMinute = currentTime.getMinutes();
  const currentTimeString = `${currentHour.toString().padStart(2, '0')}:${currentMinute.toString().padStart(2, '0')}`;

  const getClassStatus = (startTime: string, endTime: string): 'completed' | 'in-progress' | 'upcoming' => {
    if (currentTimeString > endTime) return 'completed';
    if (currentTimeString >= startTime && currentTimeString <= endTime) return 'in-progress';
    return 'upcoming';
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'in-progress':
        return 'bg-blue-100 text-blue-800';
      case 'upcoming':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getBorderColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'border-l-green-500';
      case 'in-progress':
        return 'border-l-blue-500';
      case 'upcoming':
        return 'border-l-gray-300';
      default:
        return 'border-l-gray-300';
    }
  };

  if (classesLoading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-32 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  const todayDate = new Date().toLocaleDateString('en-US', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold text-gray-800">Today's Schedule</h1>
          <p className="text-gray-600" data-testid="text-current-date">{todayDate}</p>
        </div>
        <div className="text-sm text-gray-600">
          <i className="fas fa-clock mr-2"></i>
          Current Time: {currentTimeString}
        </div>
      </div>

      {/* Sync Status */}
      <Card className="border-green-200 bg-green-50">
        <CardContent className="pt-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <i className="fas fa-sync-alt text-green-500 mr-2"></i>
              <span className="text-sm text-green-700 font-medium">All data synced</span>
            </div>
            <div className="text-xs text-green-600" data-testid="text-last-sync">
              Last sync: 2 minutes ago
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Class Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {todayClasses && todayClasses.length > 0 ? todayClasses.map((classItem) => {
          const status = getClassStatus(classItem.startTime, classItem.endTime);

          return (
            <Card key={classItem.id} className={`border-l-4 ${getBorderColor(status)}`} data-testid={`card-class-${classItem.id}`}>
              <CardContent className="pt-6">
                <div className="flex items-center justify-between mb-2">
                  <h3 className="font-medium text-gray-900" data-testid={`text-subject-${classItem.id}`}>
                    {classItem.subject}
                  </h3>
                  <Badge className={getStatusColor(status)} data-testid={`badge-status-${classItem.id}`}>
                    {status === 'in-progress' ? 'In Progress' : 
                     status === 'completed' ? 'Completed' : 'Upcoming'}
                  </Badge>
                </div>
                <p className="text-sm text-gray-600 mb-1" data-testid={`text-time-${classItem.id}`}>
                  {classItem.startTime} - {classItem.endTime}
                </p>
                <p className="text-xs text-gray-500 mb-3" data-testid={`text-students-${classItem.id}`}>
                  {classItem.name}
                </p>

                {status === 'completed' && classItem.attendanceCount && (
                  <div className="mb-3">
                    <span className="text-xs text-green-600" data-testid={`text-attendance-${classItem.id}`}>
                      Attendance: {classItem.attendanceCount.present}/{classItem.attendanceCount.total}
                    </span>
                  </div>
                )}

                <div className="mt-2">
                  {status === 'in-progress' ? (
                    <Link href="/attendance">
                      <Button className="w-full" size="sm" data-testid={`button-take-attendance-${classItem.id}`}>
                        Take Attendance
                      </Button>
                    </Link>
                  ) : status === 'upcoming' ? (
                    <Button 
                      className="w-full" 
                      size="sm" 
                      variant="outline" 
                      disabled
                      data-testid={`button-not-started-${classItem.id}`}
                    >
                      Not Started
                    </Button>
                  ) : (
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className="w-full text-blue-600"
                      data-testid={`button-view-details-${classItem.id}`}
                    >
                      View Details
                    </Button>
                  )}
                </div>
              </CardContent>
            </Card>
          );
        }) : (
          <div className="col-span-full text-center py-12">
            <i className="fas fa-calendar-alt text-4xl text-gray-400 mb-4"></i>
            <p className="text-gray-500">No classes scheduled for today</p>
          </div>
        )}
      </div>




    </div>
  );
}

client/src/pages/teacher/DigitalDiary.ts.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Plus, BookOpen, Users, Calendar, AlertCircle, CheckCircle2, Clock, Edit, Trash2 } from "lucide-react";

import { useToast } from "@/hooks/use-toast";

const announcementSchema = z.object({
  title: z.string().min(1, "Title is required"),
  content: z.string().min(1, "Content is required"),
  type: z.enum(["homework", "notice", "announcement"]),
  priority: z.enum(["low", "medium", "high"]),
  subjectId: z.string().optional(),
  classId: z.string().optional(),
  dueDate: z.string().optional(),
  recipients: z.array(z.string()).optional(),
  broadcastType: z.enum(["all", "class", "specific"]).default("all"),
});

type AnnouncementFormData = z.infer<typeof announcementSchema>;

export default function DigitalDiary() {
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [selectedFilter, setSelectedFilter] = useState<"all" | "homework" | "notice" | "announcement">("all");
  const [broadcastType, setBroadcastType] = useState<"all" | "class" | "specific">("all");
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const form = useForm<AnnouncementFormData>({
    resolver: zodResolver(announcementSchema),
    defaultValues: {
      title: "",
      content: "",
      type: "announcement",
      priority: "medium",
      subjectId: "",
      classId: "",
      dueDate: "",
      recipients: [],
      broadcastType: "all",
    },
  });

  // Fetch teacher's announcements only
  const { data: announcements = [], isLoading } = useQuery({
    queryKey: ["/api/announcements"],
    queryFn: async () => {
      const response = await fetch("/api/announcements");
      return response.json();
    },
  });

  // Fetch teacher's assigned subjects only
  const { data: subjects = [] } = useQuery({
    queryKey: ["/api/teacher/subjects"],
    queryFn: async () => {
      const response = await fetch("/api/teacher/subjects");
      return response.json();
    },
  });

  // Fetch teacher's students grouped by subject (this replaces the generic classes)
  const { data: teacherStudents = [] } = useQuery({
    queryKey: ["/api/teacher/students"],
    queryFn: async () => {
      const response = await fetch("/api/teacher/students");
      return response.json();
    },
  });

  // Use teacherStudents as students (avoid duplicate query)

  // Create announcement mutation
  const createAnnouncementMutation = useMutation({
    mutationFn: async (data: AnnouncementFormData) => {
      // Prepare announcement data with recipient logic
      const announcementData = { ...data };

      // Determine recipients based on broadcast type (restricted to teacher's students)
      if (data.broadcastType === "all") {
        // Get teacher's students only (not all students in school)
        const studentsResponse = await fetch("/api/teacher/students");
        const teacherStudents = await studentsResponse.json();
        announcementData.recipients = teacherStudents.map((student: any) => student.id);
      } else if (data.broadcastType === "class" && data.classId) {
        // Get students in specific subject that this teacher teaches
        const teacherStudentsResponse = await fetch("/api/teacher/students");
        const teacherStudents = await teacherStudentsResponse.json();
        // Filter students by the selected subject
        const subjectStudents = teacherStudents.filter((s: any) => 
          s.enrolledSubjects && s.enrolledSubjects.some((sub: any) => sub.subjectId === data.classId)
        );
        announcementData.recipients = subjectStudents.map((student: any) => student.id);
      } else if (data.broadcastType === "specific") {
        // Use manually selected recipients (already filtered to teacher's students in UI)
        announcementData.recipients = data.recipients || [];
      }

      console.log("Creating announcement with data:", announcementData);

      const response = await fetch("/api/announcements", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(announcementData),
      });
      if (!response.ok) throw new Error("Failed to create announcement");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/announcements"] });
      setIsCreateDialogOpen(false);
      form.reset();
      setBroadcastType("all");
      toast({
        title: "Success",
        description: "Announcement created successfully!",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to create announcement",
        variant: "destructive",
      });
    },
  });

  // Delete announcement mutation
  const deleteAnnouncementMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/announcements/${id}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error("Failed to delete announcement");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/announcements"] });
      toast({
        title: "Success",
        description: "Announcement deleted successfully!",
      });
    },
  });

  const onSubmit = (data: AnnouncementFormData) => {
    // Add broadcast type from state to form data
    const submitData = { ...data, broadcastType };
    createAnnouncementMutation.mutate(submitData);
  };

  const filteredAnnouncements = Array.isArray(announcements) 
    ? announcements.filter((announcement: any) => 
        selectedFilter === "all" || announcement.type === selectedFilter
      )
    : [];

  const getTypeIcon = (type: string) => {
    switch (type) {
      case "homework": return <BookOpen className="h-4 w-4" />;
      case "notice": return <AlertCircle className="h-4 w-4" />;
      case "reminder": return <Clock className="h-4 w-4" />;
      default: return <Users className="h-4 w-4" />;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case "high": return "destructive";
      case "medium": return "default";
      default: return "secondary";
    }
  };

  return (
    <div className="container mx-auto px-4 py-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Digital Diary</h1>
          <p className="text-muted-foreground">Create and manage announcements for your students</p>
        </div>

        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-announcement">
              <Plus className="h-4 w-4 mr-2" />
              Create Announcement
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Create New Announcement</DialogTitle>
            </DialogHeader>

            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="title"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Title</FormLabel>
                      <FormControl>
                        <Input {...field} placeholder="Enter announcement title" data-testid="input-title" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="content"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Content</FormLabel>
                      <FormControl>
                        <Textarea {...field} placeholder="Enter announcement content" rows={4} data-testid="textarea-content" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="type"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Type</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-type">
                              <SelectValue placeholder="Select type" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="homework">Homework</SelectItem>
                            <SelectItem value="notice">Notice</SelectItem>
                            <SelectItem value="announcement">Announcement</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="priority"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Priority</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-priority">
                              <SelectValue placeholder="Select priority" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="low">Low</SelectItem>
                            <SelectItem value="medium">Medium</SelectItem>
                            <SelectItem value="high">High</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="subjectId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Subject (Optional)</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-subject">
                              <SelectValue placeholder="Select subject" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {Array.isArray(subjects) && subjects.map((subject: any) => (
                              <SelectItem key={subject.id} value={subject.id}>
                                {subject.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="dueDate"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Due Date (Optional)</FormLabel>
                        <FormControl>
                          <Input {...field} type="date" data-testid="input-due-date" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Recipients Section */}
                <div className="space-y-4 p-4 bg-gray-50 rounded-lg">
                  <h4 className="font-medium text-gray-900 flex items-center gap-2">
                    <Users className="h-4 w-4" />
                    Recipients
                  </h4>

                  <div className="space-y-3">
                    <label className="text-sm font-medium">Send to:</label>
                    <div className="space-y-2">
                      <label className="flex items-center space-x-2">
                        <input
                          type="radio"
                          name="broadcast"
                          value="all"
                          checked={broadcastType === "all"}
                          onChange={(e) => setBroadcastType(e.target.value as "all" | "class" | "specific")}
                          className="text-blue-600"
                        />
                        <span className="text-sm">All Students</span>
                      </label>

                      <label className="flex items-center space-x-2">
                        <input
                          type="radio"
                          name="broadcast"
                          value="class"
                          checked={broadcastType === "class"}
                          onChange={(e) => setBroadcastType(e.target.value as "all" | "class" | "specific")}
                          className="text-blue-600"
                        />
                        <span className="text-sm">Specific Subject</span>
                      </label>

                      <label className="flex items-center space-x-2">
                        <input
                          type="radio"
                          name="broadcast"
                          value="specific"
                          checked={broadcastType === "specific"}
                          onChange={(e) => setBroadcastType(e.target.value as "all" | "class" | "specific")}
                          className="text-blue-600"
                        />
                        <span className="text-sm">Select Specific Students</span>
                      </label>
                    </div>
                  </div>

                  {/* Class selector for class broadcast */}
                  {broadcastType === "class" && (
                    <FormField
                      control={form.control}
                      name="classId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Select Subject</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-subject">
                                <SelectValue placeholder="Choose a subject" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {Array.isArray(subjects) && subjects.map((subject: any) => (
                                <SelectItem key={subject.id} value={subject.id}>
                                  {subject.name} ({subject.code})
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  )}

                  {/* Student multi-selector for specific broadcast */}
                  {broadcastType === "specific" && (
                    <FormField
                      control={form.control}
                      name="recipients"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Select Students</FormLabel>
                          <div className="max-h-32 overflow-y-auto border rounded-md p-2 space-y-1">
                            {Array.isArray(teacherStudents) && teacherStudents.map((student: any) => (
                              <label key={student.id} className="flex items-center space-x-2 text-sm">
                                <input
                                  type="checkbox"
                                  checked={field.value?.includes(student.id) || false}
                                  onChange={(e) => {
                                    const currentValue = field.value || [];
                                    if (e.target.checked) {
                                      field.onChange([...currentValue, student.id]);
                                    } else {
                                      field.onChange(currentValue.filter(id => id !== student.id));
                                    }
                                  }}
                                  className="text-blue-600"
                                />
                                <span>{student.firstName} {student.lastName} ({student.classLevel})</span>
                              </label>
                            ))}
                          </div>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  )}
                </div>

                <div className="flex justify-end space-x-2">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setIsCreateDialogOpen(false)}
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createAnnouncementMutation.isPending}
                    data-testid="button-submit"
                  >
                    {createAnnouncementMutation.isPending ? "Creating..." : "Create Announcement"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Filter tabs */}
      <div className="flex space-x-2">
        {["all", "homework", "notice", "announcement"].map((filter) => (
          <Button
            key={filter}
            variant={selectedFilter === filter ? "default" : "outline"}
            onClick={() => setSelectedFilter(filter as any)}
            className="capitalize"
            data-testid={`filter-${filter}`}
          >
            {filter === "all" ? "All" : filter}
          </Button>
        ))}
      </div>

      {/* Announcements list */}
      <div className="space-y-4">
        {isLoading ? (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
            <p className="mt-2 text-muted-foreground">Loading announcements...</p>
          </div>
        ) : filteredAnnouncements.length === 0 ? (
          <Card>
            <CardContent className="text-center py-8">
              <Users className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-semibold mb-2">No announcements found</h3>
              <p className="text-muted-foreground mb-4">
                {selectedFilter === "all" 
                  ? "Create your first announcement to get started"
                  : `No ${selectedFilter} announcements found`
                }
              </p>
              <Button onClick={() => setIsCreateDialogOpen(true)} data-testid="button-create-first">
                <Plus className="h-4 w-4 mr-2" />
                Create Announcement
              </Button>
            </CardContent>
          </Card>
        ) : (
          filteredAnnouncements.map((announcement: any) => (
            <Card key={announcement.id} data-testid={`card-announcement-${announcement.id}`}>
              <CardHeader className="pb-2">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    {getTypeIcon(announcement.type)}
                    <CardTitle className="text-lg">{announcement.title}</CardTitle>
                    <Badge variant={getPriorityColor(announcement.priority) as any}>
                      {announcement.priority}
                    </Badge>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Badge variant="outline" className="capitalize">
                      {announcement.type}
                    </Badge>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => deleteAnnouncementMutation.mutate(announcement.id)}
                      data-testid={`button-delete-${announcement.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
                <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                  <span>Created: {new Date(announcement.createdAt).toLocaleDateString()}</span>
                  {announcement.dueDate && (
                    <span className="flex items-center">
                      <Calendar className="h-3 w-3 mr-1" />
                      Due: {new Date(announcement.dueDate).toLocaleDateString()}
                    </span>
                  )}
                </div>
              </CardHeader>
              <CardContent>
                <p className="text-gray-700 whitespace-pre-wrap" data-testid={`text-content-${announcement.id}`}>
                  {announcement.content}
                </p>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/pages/teacher/Earnings.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { TeacherEarnings, CashDrawRequest } from "@/types";

export default function Earnings() {
  const [requestAmount, setRequestAmount] = useState("");
  const [requestReason, setRequestReason] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: earnings, isLoading: earningsLoading } = useQuery<TeacherEarnings>({
    queryKey: ['/api/teacher/earnings'],
  });

  const { data: cashDrawRequests } = useQuery<CashDrawRequest[]>({
    queryKey: ['/api/cash-draw-requests'],
  });

  const createRequestMutation = useMutation({
    mutationFn: async (data: { amount: number; reason: string }) => {
      return await apiRequest('POST', '/api/cash-draw-requests', data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Cash draw request submitted successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/cash-draw-requests'] });
      setRequestAmount("");
      setRequestReason("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit request. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmitRequest = () => {
    if (!requestAmount || !requestReason.trim()) {
      toast({
        title: "Validation Error",
        description: "Please enter both amount and reason.",
        variant: "destructive",
      });
      return;
    }

    const amount = parseFloat(requestAmount);
    if (amount <= 0 || amount > (earnings?.total || 0)) {
      toast({
        title: "Validation Error",
        description: "Please enter a valid amount within your available earnings.",
        variant: "destructive",
      });
      return;
    }

    createRequestMutation.mutate({
      amount,
      reason: requestReason,
    });
  };

  const myRequests = cashDrawRequests?.filter(request => 
    request.teacherId === 'current-teacher-id' // This should be the current user's ID
  ) || [];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'approved':
        return 'bg-green-100 text-green-800';
      case 'denied':
        return 'bg-red-100 text-red-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'approved':
        return 'fas fa-check-circle text-green-600';
      case 'denied':
        return 'fas fa-times-circle text-red-600';
      case 'pending':
        return 'fas fa-clock text-yellow-600';
      default:
        return 'fas fa-question-circle text-gray-600';
    }
  };

  if (earningsLoading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="h-32 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Earnings Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <i className="fas fa-percentage text-blue-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Base Rate (70%)</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-base-earnings">
                  Rs. {earnings?.baseAmount?.toLocaleString() || '0'}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <i className="fas fa-plus text-green-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Extra Classes</p>
                <p className="text-2xl font-semibold text-gray-900" data-testid="stat-extra-earnings">
                  Rs. {earnings?.extraClasses?.toLocaleString() || '0'}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center">
              <div className="p-2 bg-purple-100 rounded-lg">
                <i className="fas fa-wallet text-purple-600 text-xl"></i>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Earned</p>
                <p className="text-2xl font-semibold text-purple-600" data-testid="stat-total-earnings">
                  Rs. {earnings?.total?.toLocaleString() || '0'}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Request Cash Draw */}
        <Card>
          <CardHeader>
            <CardTitle>Request Cash Draw</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="amount">Amount (Rs. )</Label>
              <Input
                id="amount"
                type="number"
                placeholder="0"
                value={requestAmount}
                onChange={(e) => setRequestAmount(e.target.value)}
                max={earnings?.total || 0}
                data-testid="input-request-amount"
              />
              <p className="text-xs text-gray-500 mt-1">
                Available: Rs. {earnings?.total?.toLocaleString() || '0'}
              </p>
            </div>

            <div>
              <Label htmlFor="reason">Reason for Request</Label>
              <Textarea
                id="reason"
                placeholder="Please provide a reason for this cash draw request..."
                value={requestReason}
                onChange={(e) => setRequestReason(e.target.value)}
                rows={3}
                data-testid="textarea-request-reason"
              />
            </div>

            <Button 
              onClick={handleSubmitRequest}
              disabled={!requestAmount || !requestReason.trim() || createRequestMutation.isPending}
              className="w-full"
              data-testid="button-submit-request"
            >
              {createRequestMutation.isPending ? 'Submitting...' : 'Submit Request'}
            </Button>
          </CardContent>
        </Card>

        {/* Earnings Breakdown */}
        <Card>
          <CardHeader>
            <CardTitle>This Month's Breakdown</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-3">
              <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                <span className="text-gray-600">Physics A-Level (24 students)</span>
                <span className="font-semibold">Rs. 12,000</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                <span className="text-gray-600">Chemistry O-Level (18 students)</span>
                <span className="font-semibold">Rs. 9,000</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                <span className="text-gray-600">Extra Sessions (5 hours)</span>
                <span className="font-semibold">Rs. 3,500</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-blue-50 rounded-lg border-t border-blue-200">
                <span className="font-semibold text-blue-800">Total This Month</span>
                <span className="font-bold text-blue-800">Rs. {earnings?.total?.toLocaleString() || '0'}</span>
              </div>
            </div>

            <div className="pt-4">
              <Button variant="outline" className="w-full" data-testid="button-download-statement">
                <i className="fas fa-download mr-2"></i>
                Download Statement
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Request History */}
      <Card>
        <CardHeader>
          <CardTitle>Request History</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <table className="w-full text-sm">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Date</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Amount</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Reason</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Status</th>
                  <th className="px-4 py-3 text-left font-medium text-gray-700">Reviewed</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {myRequests.length > 0 ? myRequests.map((request) => (
                  <tr key={request.id} className="hover:bg-gray-50" data-testid={`row-request-${request.id}`}>
                    <td className="px-4 py-3" data-testid={`text-date-${request.id}`}>
                      {new Date(request.requestedAt).toLocaleDateString()}
                    </td>
                    <td className="px-4 py-3">
                      <span className="font-semibold" data-testid={`text-amount-${request.id}`}>
                        Rs. {Number(request.amount).toLocaleString()}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <span className="text-gray-700 max-w-xs truncate" data-testid={`text-reason-${request.id}`}>
                        {request.reason}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <Badge className={getStatusColor(request.status)} data-testid={`badge-status-${request.id}`}>
                        <i className={`${getStatusIcon(request.status)} mr-1`}></i>
                        {request.status.toUpperCase()}
                      </Badge>
                    </td>
                    <td className="px-4 py-3">
                      {request.reviewedAt ? (
                        <div className="text-sm" data-testid={`text-reviewed-${request.id}`}>
                          <p>{new Date(request.reviewedAt).toLocaleDateString()}</p>
                          {request.notes && (
                            <p className="text-xs text-gray-500">{request.notes}</p>
                          )}
                        </div>
                      ) : (
                        <span className="text-gray-400 text-sm">Pending</span>
                      )}
                    </td>
                  </tr>
                )) : (
                  <tr>
                    <td colSpan={5} className="px-4 py-8 text-center text-gray-500">
                      <i className="fas fa-history text-4xl mb-4"></i>
                      <p>No cash draw requests yet</p>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/teacher/Gradebook.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Assessment, Grade } from "@shared/schema";

interface Student {
  id: string;
  firstName: string;
  lastName: string;
  rollNumber: string;
}

export default function Gradebook() {
  const [dialogSubject, setDialogSubject] = useState("");
  const [newAssessment, setNewAssessment] = useState({
    name: "",
    totalMarks: "",
    date: "",
    description: "",
  });
  const [grades, setGrades] = useState<{ [studentId: string]: { marks: string; comments: string } }>({});
  const [selectedAssessmentForGrading, setSelectedAssessmentForGrading] = useState<string | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch only teacher's assigned subjects (maintaining data isolation)
  const { data: subjects = [] } = useQuery({
    queryKey: ['/api/teacher/subjects'],
  });

  // Fetch only teacher's students (enrolled in subjects they teach)
  const { data: students = [] } = useQuery<Student[]>({
    queryKey: ['/api/teacher/students'],
  });

  // Fetch only teacher's assessments (maintaining data isolation)
  const { data: assessments = [] } = useQuery<Assessment[]>({
    queryKey: ['/api/teacher/assessments'],
  });

  const createAssessmentMutation = useMutation({
    mutationFn: async (assessmentData: any) => {
      return await apiRequest('POST', '/api/assessments', assessmentData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Assessment created successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/assessments'] });
      setNewAssessment({ name: "", totalMarks: "", date: "", description: "" });
      setDialogSubject("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create assessment. Please try again.",
        variant: "destructive",
      });
    },
  });

  const createGradesMutation = useMutation({
    mutationFn: async (gradesData: any[]) => {
      const promises = gradesData.map(grade => 
        apiRequest('POST', '/api/grades', grade)
      );
      return Promise.all(promises);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Grades submitted successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/grades'] });
      setGrades({});
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit grades. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleCreateAssessment = () => {
    console.log('Validation Debug:', {
      name: newAssessment.name,
      totalMarks: newAssessment.totalMarks,
      dialogSubject: dialogSubject,
      date: newAssessment.date
    });

    // Check if required fields are filled
    const missingFields = [];
    if (!newAssessment.name?.trim()) missingFields.push('Assessment Name');
    if (!newAssessment.totalMarks?.trim()) missingFields.push('Total Marks');
    if (!dialogSubject) missingFields.push('Subject');

    if (missingFields.length > 0) {
      console.log('Validation failed - missing fields:', missingFields);
      toast({
        title: "Validation Error",
        description: `Please fill in: ${missingFields.join(', ')}`,
        variant: "destructive",
      });
      return;
    }

    // Use dialogSubject for assessment creation
    const subjectToUse = dialogSubject;

    console.log('Creating assessment with data:', {
      name: newAssessment.name,
      subjectId: subjectToUse,
      totalMarks: parseInt(newAssessment.totalMarks),
      assessmentDate: newAssessment.date,
      description: newAssessment.description,
    });

    createAssessmentMutation.mutate({
      name: newAssessment.name,
      subjectId: subjectToUse,
      totalMarks: parseInt(newAssessment.totalMarks),
      assessmentDate: newAssessment.date,
      description: newAssessment.description,
    });
  };

  const handleGradeChange = (studentId: string, field: 'marks' | 'comments', value: string) => {
    setGrades(prev => ({
      ...prev,
      [studentId]: {
        ...prev[studentId],
        [field]: value,
      }
    }));
  };

  const calculateGrade = (marks: number, totalMarks: number): string => {
    const percentage = (marks / totalMarks) * 100;
    if (percentage >= 90) return 'A+';
    if (percentage >= 80) return 'A';
    if (percentage >= 70) return 'B+';
    if (percentage >= 60) return 'B';
    if (percentage >= 50) return 'C';
    return 'F';
  };

  const handleSubmitGrades = () => {
    if (!selectedAssessmentForGrading) return;

    const selectedAssessmentData = assessments?.find(a => a.id === selectedAssessmentForGrading);
    if (!selectedAssessmentData) return;

    const gradesData = Object.entries(grades)
      .filter(([_, data]) => data.marks.trim() !== '')
      .map(([studentId, data]) => ({
        assessmentId: selectedAssessmentForGrading,
        studentId,
        marksObtained: parseInt(data.marks),
        grade: calculateGrade(parseInt(data.marks), selectedAssessmentData.totalMarks),
        comments: data.comments,
      }));

    if (gradesData.length === 0) {
      toast({
        title: "Warning",
        description: "Please enter grades for at least one student.",
        variant: "destructive",
      });
      return;
    }

    createGradesMutation.mutate(gradesData);
  };

  const handleEnterScores = (assessmentId: string) => {
    setSelectedAssessmentForGrading(assessmentId);
    setGrades({}); // Reset grades when switching assessments
  };

  const handleBackToDashboard = () => {
    setSelectedAssessmentForGrading(null);
    setGrades({});
  };

  // Get subject name by ID
  const getSubjectName = (subjectId: string) => {
    return Array.isArray(subjects) ? subjects.find((s: any) => s.id === subjectId)?.name || 'Unknown Subject' : 'Unknown Subject';
  };

  const selectedAssessmentData = assessments?.find(a => a.id === selectedAssessmentForGrading);

  // Grade Entry View - When an assessment is selected for grading
  if (selectedAssessmentForGrading && selectedAssessmentData) {
    return (
      <div className="space-y-6">
        {/* Header with Back Button */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold">Enter Scores</h1>
            <p className="text-gray-600 mt-1">
              {selectedAssessmentData.name} - {getSubjectName(selectedAssessmentData.subjectId)}
            </p>
          </div>
          <Button variant="outline" onClick={handleBackToDashboard} data-testid="button-back-dashboard">
            <i className="fas fa-arrow-left mr-2"></i>
            Back to Dashboard
          </Button>
        </div>

        {/* Student List for Grade Entry */}
        <Card>
          <CardHeader>
            <CardTitle>Student List</CardTitle>
            <p className="text-sm text-gray-600">
              Enter the marks obtained by each student. Total marks for this assessment are{' '}
              <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded font-medium">
                {selectedAssessmentData.totalMarks}
              </span>
              .
            </p>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="grid grid-cols-4 gap-4 pb-2 border-b font-medium text-gray-700">
                <div>Student ID</div>
                <div>Student Name</div>
                <div></div>
                <div>Score</div>
              </div>

              {students?.map((student) => {
                const studentGrade = grades[student.id];
                const marks = studentGrade?.marks || '';

                return (
                  <div key={student.id} className="grid grid-cols-4 gap-4 items-center py-3 border-b border-gray-100">
                    <div className="font-medium" data-testid={`text-student-id-${student.id}`}>
                      {student.rollNumber}
                    </div>
                    <div className="flex items-center">
                      <div className="w-10 h-10 bg-gray-200 rounded-full flex items-center justify-center text-sm font-medium text-gray-600 mr-3">
                        {student.firstName.charAt(0)}{student.lastName.charAt(0)}
                      </div>
                      <span data-testid={`text-student-name-${student.id}`}>
                        {student.firstName} {student.lastName}
                      </span>
                    </div>
                    <div></div>
                    <div className="flex items-center">
                      <Input
                        type="number"
                        placeholder="0"
                        value={marks}
                        onChange={(e) => handleGradeChange(student.id, 'marks', e.target.value)}
                        className="w-20 text-center"
                        max={selectedAssessmentData.totalMarks}
                        min="0"
                        data-testid={`input-score-${student.id}`}
                      />
                      <span className="ml-2 text-gray-500">/ {selectedAssessmentData.totalMarks}</span>
                    </div>
                  </div>
                );
              })}
            </div>

            <div className="mt-6 flex justify-end">
              <Button 
                onClick={handleSubmitGrades}
                disabled={createGradesMutation.isPending}
                data-testid="button-submit-grades"
              >
                {createGradesMutation.isPending ? 'Submitting...' : 'Save Grades'}
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Main Assessments Table View
  return (
    <div className="space-y-6">
      {/* Header with Create Button */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Gradebook</h1>
        </div>
        <Dialog>
          <DialogTrigger asChild>
            <Button data-testid="button-create-assessment">
              <i className="fas fa-plus mr-2"></i>
              Create New Assessment
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New Assessment</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <Label htmlFor="assessmentSubject">Subject *</Label>
                <Select 
                  value={dialogSubject} 
                  onValueChange={(value) => {
                    console.log('Subject selected:', value);
                    setDialogSubject(value);
                  }}
                >
                  <SelectTrigger data-testid="select-assessment-subject">
                    <SelectValue placeholder="Choose a subject..." />
                  </SelectTrigger>
                  <SelectContent>
                    {Array.isArray(subjects) && subjects.map((subject: any) => (
                      <SelectItem key={subject.id} value={subject.id}>
                        {subject.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="assessmentName">Assessment Name *</Label>
                <Input
                  id="assessmentName"
                  placeholder="e.g., Quiz 1, Midterm Exam"
                  value={newAssessment.name}
                  onChange={(e) => {
                    console.log('Assessment name changed:', e.target.value);
                    setNewAssessment(prev => ({ ...prev, name: e.target.value }));
                  }}
                  data-testid="input-assessment-name"
                />
              </div>

              <div>
                <Label htmlFor="totalMarks">Total Marks *</Label>
                <Input
                  id="totalMarks"
                  type="number"
                  placeholder="100"
                  value={newAssessment.totalMarks}
                  onChange={(e) => {
                    console.log('Total marks changed:', e.target.value);
                    setNewAssessment(prev => ({ ...prev, totalMarks: e.target.value }));
                  }}
                  data-testid="input-total-marks"
                />
              </div>

              <div>
                <Label htmlFor="assessmentDate">Assessment Date</Label>
                <Input
                  id="assessmentDate"
                  type="date"
                  value={newAssessment.date}
                  onChange={(e) => setNewAssessment(prev => ({ ...prev, date: e.target.value }))}
                  data-testid="input-assessment-date"
                />
              </div>

              <div>
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  placeholder="Optional description..."
                  value={newAssessment.description}
                  onChange={(e) => setNewAssessment(prev => ({ ...prev, description: e.target.value }))}
                  data-testid="textarea-description"
                />
              </div>

              <div className="flex justify-end space-x-2">
                <Button variant="outline">Cancel</Button>
                <Button 
                  onClick={handleCreateAssessment}
                  disabled={createAssessmentMutation.isPending}
                  data-testid="button-save-assessment"
                >
                  {createAssessmentMutation.isPending ? 'Creating...' : 'Create Assessment'}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Assessments Table */}
      <Card>
        <CardHeader>
          <CardTitle>Assessments</CardTitle>
          <p className="text-sm text-gray-600">View and manage your created assessments.</p>
        </CardHeader>
        <CardContent>
          {assessments && assessments.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Title</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Subject</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Date</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Entries</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Total Marks</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {assessments.map((assessment) => (
                    <tr key={assessment.id} data-testid={`row-assessment-${assessment.id}`}>
                      <td className="px-4 py-3">
                        <div className="font-medium" data-testid={`text-assessment-title-${assessment.id}`}>
                          {assessment.name}
                        </div>
                      </td>
                      <td className="px-4 py-3" data-testid={`text-assessment-subject-${assessment.id}`}>
                        {getSubjectName(assessment.subjectId)}
                      </td>
                      <td className="px-4 py-3" data-testid={`text-assessment-date-${assessment.id}`}>
                        {new Date(assessment.assessmentDate).toLocaleDateString()}
                      </td>
                      <td className="px-4 py-3" data-testid={`text-assessment-entries-${assessment.id}`}>
                        0 / {students?.length || 0}
                      </td>
                      <td className="px-4 py-3" data-testid={`text-assessment-marks-${assessment.id}`}>
                        {assessment.totalMarks}
                      </td>
                      <td className="px-4 py-3">
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => handleEnterScores(assessment.id)}
                          data-testid={`button-enter-scores-${assessment.id}`}
                        >
                          <i className="fas fa-edit mr-1"></i>
                          Enter Scores
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-8 text-gray-500">
              <p>No assessments created yet.</p>
              <p className="text-sm mt-1">Click "Create New Assessment" to get started.</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/teacher/ScheduleManager.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { 
  Plus, 
  Calendar, 
  Clock, 
  MapPin, 
  Edit, 
  Trash2, 
  AlertCircle
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

const scheduleSchema = z.object({
  subjectId: z.string().min(1, "Subject is required"),
  dayOfWeek: z.enum(["monday", "tuesday", "wednesday", "thursday", "friday", "saturday", "sunday"]),
  startTime: z.string().min(1, "Start time is required"),
  endTime: z.string().min(1, "End time is required"),
  location: z.string().optional(),
});



type ScheduleFormData = z.infer<typeof scheduleSchema>;

const dayLabels = {
  monday: "Monday",
  tuesday: "Tuesday", 
  wednesday: "Wednesday",
  thursday: "Thursday",
  friday: "Friday",
  saturday: "Saturday",
  sunday: "Sunday"
};

export default function ScheduleManager() {
  const [isScheduleDialogOpen, setIsScheduleDialogOpen] = useState(false);
  const [editingSchedule, setEditingSchedule] = useState<any>(null);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const scheduleForm = useForm<ScheduleFormData>({
    resolver: zodResolver(scheduleSchema),
    defaultValues: {
      subjectId: "",
      dayOfWeek: "monday",
      startTime: "",
      endTime: "",
      location: "",
    },
  });



  // Fetch teacher's schedules
  const { data: schedules = [], isLoading: schedulesLoading } = useQuery({
    queryKey: ["/api/teacher/schedules"],
    queryFn: async () => {
      const response = await fetch("/api/teacher/schedules");
      const data = await response.json();
      // Return empty array if the response is an error or not an array
      return Array.isArray(data) ? data : [];
    },
  });

  // Fetch teacher's subjects
  const { data: subjects = [] } = useQuery({
    queryKey: ["/api/teacher/subjects"],
    queryFn: async () => {
      const response = await fetch("/api/teacher/subjects");
      return response.json();
    },
  });



  // Create/Update schedule mutation
  const scheduleUpsertMutation = useMutation({
    mutationFn: async (data: ScheduleFormData & { id?: string }) => {
      if (data.id) {
        // Update existing schedule
        return apiRequest("PUT", `/api/teacher/schedules/${data.id}`, data);
      } else {
        // Create new schedule
        return apiRequest("POST", "/api/teacher/schedules", data);
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/teacher/schedules"] });
      setIsScheduleDialogOpen(false);
      setEditingSchedule(null);
      scheduleForm.reset();
      toast({
        title: "Success",
        description: editingSchedule ? "Schedule updated successfully!" : "Schedule created successfully!",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to save schedule",
        variant: "destructive",
      });
    },
  });

  // Delete schedule mutation
  const deleteScheduleMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/teacher/schedules/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/teacher/schedules"] });
      toast({
        title: "Success",
        description: "Schedule deleted successfully!",
      });
    },
    onError: (error) => {
      toast({
        title: "Error", 
        description: "Failed to delete schedule",
        variant: "destructive",
      });
    },
  });



  const onScheduleSubmit = (data: ScheduleFormData) => {
    scheduleUpsertMutation.mutate(editingSchedule ? { ...data, id: editingSchedule.id } : data);
  };



  const handleEditSchedule = (schedule: any) => {
    setEditingSchedule(schedule);
    scheduleForm.reset({
      subjectId: schedule.subjectId,
      dayOfWeek: schedule.dayOfWeek,
      startTime: schedule.startTime,
      endTime: schedule.endTime,
      location: schedule.location || "",
    });
    setIsScheduleDialogOpen(true);
  };

  const handleDeleteSchedule = (id: string) => {
    if (confirm("Are you sure you want to delete this schedule?")) {
      deleteScheduleMutation.mutate(id);
    }
  };



  // Group schedules by day
  const schedulesByDay = Array.isArray(schedules) ? schedules.reduce((acc: any, schedule: any) => {
    if (!acc[schedule.dayOfWeek]) {
      acc[schedule.dayOfWeek] = [];
    }
    acc[schedule.dayOfWeek].push(schedule);
    return acc;
  }, {}) : {};

  // Sort each day's schedules by start time
  Object.keys(schedulesByDay).forEach(day => {
    schedulesByDay[day].sort((a: any, b: any) => a.startTime.localeCompare(b.startTime));
  });

  return (
    <div className="container mx-auto px-4 py-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Schedule Manager</h1>
          <p className="text-muted-foreground">Manage your class schedules</p>
        </div>

        <Dialog open={isScheduleDialogOpen} onOpenChange={setIsScheduleDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-add-schedule">
              <Plus className="h-4 w-4 mr-2" />
              Add Schedule
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-md">
            <DialogHeader>
              <DialogTitle>{editingSchedule ? "Edit Schedule" : "Add New Schedule"}</DialogTitle>
            </DialogHeader>
            <Form {...scheduleForm}>
              <form onSubmit={scheduleForm.handleSubmit(onScheduleSubmit)} className="space-y-4">
                <FormField
                  control={scheduleForm.control}
                  name="subjectId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Subject</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-subject">
                            <SelectValue placeholder="Select subject" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {subjects.map((subject: any) => (
                            <SelectItem key={subject.id} value={subject.id}>
                              {subject.name} ({subject.code})
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={scheduleForm.control}
                  name="dayOfWeek"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Day of Week</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-day">
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {Object.entries(dayLabels).map(([value, label]) => (
                            <SelectItem key={value} value={value}>
                              {label}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={scheduleForm.control}
                    name="startTime"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Start Time</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="time"
                            data-testid="input-start-time"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={scheduleForm.control}
                    name="endTime"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>End Time</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="time"
                            data-testid="input-end-time"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={scheduleForm.control}
                  name="location"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Location (Optional)</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          placeholder="Room 101, Lab A, etc."
                          data-testid="input-location"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex justify-end gap-3">
                  <Button type="button" variant="outline" onClick={() => setIsScheduleDialogOpen(false)}>
                    Cancel
                  </Button>
                  <Button type="submit" disabled={scheduleUpsertMutation.isPending} data-testid="button-save-schedule">
                    {scheduleUpsertMutation.isPending ? "Saving..." : (editingSchedule ? "Update" : "Create")}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Regular Schedules */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5" />
            Weekly Schedule
          </CardTitle>
        </CardHeader>
        <CardContent>
          {schedulesLoading ? (
            <div className="text-center py-8">Loading schedules...</div>
          ) : schedules.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              No schedules created yet. Click "Add Schedule" to get started.
            </div>
          ) : (
            <div className="grid gap-4">
              {Object.entries(dayLabels).map(([day, label]) => (
                <div key={day} className="border rounded-lg p-4">
                  <h3 className="font-semibold mb-3">{label}</h3>
                  {schedulesByDay[day]?.length > 0 ? (
                    <div className="grid gap-3">
                      {schedulesByDay[day].map((schedule: any) => (
                        <div key={schedule.id} className="flex items-center justify-between p-3 bg-muted rounded-lg" data-testid={`schedule-item-${schedule.id}`}>
                          <div className="flex items-center gap-4">
                            <div className="flex items-center gap-2">
                              <Clock className="h-4 w-4 text-muted-foreground" />
                              <span className="font-medium">
                                {schedule.startTime} - {schedule.endTime}
                              </span>
                            </div>
                            <Badge variant="outline">{schedule.subjectName}</Badge>
                            {schedule.location && (
                              <div className="flex items-center gap-1 text-sm text-muted-foreground">
                                <MapPin className="h-3 w-3" />
                                {schedule.location}
                              </div>
                            )}
                          </div>
                          <div className="flex gap-2">
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleEditSchedule(schedule)}
                              data-testid={`button-edit-${schedule.id}`}
                            >
                              <Edit className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleDeleteSchedule(schedule.id)}
                              data-testid={`button-delete-${schedule.id}`}
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-muted-foreground text-sm">No classes scheduled</div>
                  )}
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>


    </div>
  );
}

